mapper.select:
SELECT B.STAFF_SEQ AS CODE_CD, STAFF_NM AS CODE_NM FROM TBL_COMPANY A INNER JOIN TBL_COMPANY_STAFF B ON A.COMP_CD = B.COMP_CD AND B.DEL_YN = 'N' WHERE 1 = 1
AND A.COMP_CD = #{COMP_CD}
AND A.DEL_YN = 'N'

with recursive empty_tbl as (
select 1 as ROWNUM
union all
select ROWNUM + 1  from empty_tbl where ROWNUM < ${COUNT}
)
select ROWNUM from empty_tbl;
