mapper.select:
SELECT A.COMP_CD, B.STAFF_SEQ AS CODE_CD, B.STAFF_NM AS CODE_NM FROM TBL_COMPANY A INNER JOIN TBL_COMPANY_STAFF B ON A.COMP_CD = B.COMP_CD AND B.DEL_YN = 'N' WHERE 1 = 1
AND A.COMP_CD = #{COMP_CD}
AND A.DEL_YN = 'N'

with recursive empty_tbl as (
select 1 as ROWNUM
union all
select ROWNUM + 1  from empty_tbl where ROWNUM < ${COUNT}
)
select ROWNUM from empty_tbl;

SELECT B.USER_NM AS CODE_NM, B.USER_ID AS CODE_CD FROM TBL_COMPANY A INNER JOIN TBL_USER B ON A.CHARGE_USER_ID = B.USER_ID AND B.DEL_YN = 'N' WHERE A.DEL_YN = 'N'
AND A.COMP_CD = #{COMP_CD}
ORDER BY B.USER_NM
