mapper.select:
SELECT A.EQUIP_SEQ , A.EQUIP_ID , A.PHOTO_GFILE_SEQ , jmes.SF_GET_FILE_ORGINAL_NM(A.PHOTO_GFILE_SEQ, NULL) as PHOTO_GFILE_SEQ_NM , A.EQUIP_NM , jmes.SF_GET_CODE_NM('1010', A.PROCESS_TYPE, 'KR') AS PROCESS_TYPE_NM , jmes.SF_GET_CODE_NM('1012', A.EQUIP_TYPE, 'KR') AS EQUIP_TYPE_NM -- , jmes.SF_GET_CODE_NM('1034', A.EQUIP_SIZE, 'KR') AS EQUIP_SIZE_NM , A.EQUIP_SIZE , jmes.SF_GET_CODE_NM('1037', A.EQUIP_COMP_CD, 'KR') AS EQUIP_COMP_NM , jmes.SF_GET_CODE_NM('1022', A.RESELLER_CD, 'KR') AS RESELLER_NM , jmes.SF_GET_CODE_NM('1005', A.FACTORY_AREA, 'KR') AS FACTORY_AREA_NM , jmes.SF_GET_CODE_NM('1014', (SELECT REF_CD FROM jmes.TBL_CODE WHERE HIGH_CD = '1005' AND CODE_CD = A.FACTORY_AREA), 'KR') AS FACTORY_NM , jmes.SF_GET_DATE_F(A.PURCHASE_DT,'YY','') AS PURCHASE_DT , (SELECT USER_NM FROM jmes.TBL_USER WHERE USER_ID = A.MAIN_USER_ID) AS MAIN_USER_NM , (SELECT USER_NM FROM jmes.TBL_USER WHERE USER_ID = A.SUB_USER_ID) AS SUB_USER_NM , 'AFTER...' AS WORKING_TIME , (SELECT jmes.SF_GET_DATE_F(MAX(REPAIR_END_DT),'YY','') FROM jmes.TBL_EQUIP_REPAIR WHERE EQUIP_SEQ = A.EQUIP_SEQ) AS LAST_REPAIR_DT , 'AFTER...' AS LAST_CONTROL_NUM , A.NOTE , A.MODEL_NUM, A.SERIAL_NUM FROM jmes.TBL_EQUIP A WHERE A.EQUIP_KIND = #{SEL_EQUIP_KIND} AND A.DEL_YN = 'N'
AND A.EQUIP_TYPE = #{SEL_EQUIP_TYPE}
AND A.EQUIP_SIZE = #{SEL_EQUIP_SIZE}
AND A.EQUIP_NM LIKE CONCAT('%', #{SEL_EQUIP_NM}, '%')
AND A.FACTORY_AREA = #{SEL_FACTORY_AREA}
AND EXISTS ( SELECT 1 FROM jmes.TBL_USER WHERE USER_ID IN (A.MAIN_USER_ID, A.SUB_USER_ID) AND USER_NM LIKE CONCAT('%', #{SEL_MAIN_USER_ID}, '%') )
AND A.PURCHASE_DT >= REPLACE(#{SEL_ST_DT},'-',space(0)) AND A.PURCHASE_DT <= REPLACE(#{SEL_END_DT},'-',space(0))
ORDER BY A.EQUIP_ID

mapper.insert:
INSERT INTO jmes.TBL_EQUIP (EQUIP_SEQ, EQUIP_ID, EQUIP_KIND, EQUIP_NM
                      , PROCESS_TYPE, EQUIP_TYPE, EQUIP_SIZE
                      , EQUIP_COMP_CD, FACTORY_AREA
                      , MAIN_USER_ID, SUB_USER_ID, PURCHASE_DT, RESELLER_CD, NOTE
                      , PHOTO_GFILE_SEQ, ETC_GFILE_SEQ, INSERT_ID, MODEL_NUM, SERIAL_NUM)
VALUES (  #{EQUIP_SEQ}, #{EQUIP_ID}, #{EQUIP_KIND}, #{EQUIP_NM}
        , #{PROCESS_TYPE, jdbcType=VARCHAR}, #{EQUIP_TYPE}, #{EQUIP_SIZE}
        , #{EQUIP_COMP_CD}, #{FACTORY_AREA}
        , #{MAIN_USER_ID}, #{SUB_USER_ID}, REPLACE(#{PURCHASE_DT},'-',space(0)), #{RESELLER_CD}, #{NOTE}
        , #{PHOTO_GFILE_SEQ}, #{ETC_GFILE_SEQ}, #{LOGIN_USER_ID}, #{MODEL_NUM}, #{SERIAL_NUM} )

mapper.update:
UPDATE jmes.TBL_EQUIP
SET EQUIP_ID = #{EQUIP_ID}
      , EQUIP_NM = #{EQUIP_NM}
      , PROCESS_TYPE = #{PROCESS_TYPE, jdbcType=VARCHAR}
      , EQUIP_TYPE = #{EQUIP_TYPE}
      , EQUIP_SIZE = #{EQUIP_SIZE}
      , EQUIP_COMP_CD = #{EQUIP_COMP_CD}
      , FACTORY_AREA = #{FACTORY_AREA}
      , MAIN_USER_ID = #{MAIN_USER_ID}
      , SUB_USER_ID = #{SUB_USER_ID}
      , PURCHASE_DT = REPLACE(#{PURCHASE_DT},'-',space(0))
      , RESELLER_CD = #{RESELLER_CD}
      , NOTE = #{NOTE}
      , MODEL_NUM = #{MODEL_NUM}
      , SERIAL_NUM = #{SERIAL_NUM}

          , PHOTO_GFILE_SEQ = #{PHOTO_GFILE_SEQ}


      , ETC_GFILE_SEQ = #{ETC_GFILE_SEQ}

      , UPDATE_DT = NOW()
      , UPDATE_ID = #{LOGIN_USER_ID}
WHERE EQUIP_SEQ = #{EQUIP_SEQ}

mapper.delete:
UPDATE jmes.TBL_EQUIP
SET DEL_YN = 'Y'
  , UPDATE_DT = NOW()
  , UPDATE_ID = #{LOGIN_USER_ID}
WHERE EQUIP_SEQ = #{EQUIP_SEQ}

mapper.update:
UPDATE jmes.TBL_EQUIP_REPAIR
SET  REPAIR_TYPE = #{REPAIR_TYPE}
    , REPAIR_START_DT = STR_TO_DATE(#{REPAIR_START_DT},'%Y-%m-%d %H:%i')
    , REPAIR_END_DT = STR_TO_DATE(#{REPAIR_END_DT},'%Y-%m-%d %H:%i')
    , REPAIR_DESC = #{REPAIR_DESC}
    , NOTE = #{NOTE}
    , BEFORE_GFILE_SEQ = #{BEFORE_GFILE_SEQ}
    , AFTER_GFILE_SEQ = #{AFTER_GFILE_SEQ}
    , UPDATE_DT = NOW()
    , UPDATE_ID = #{LOGIN_USER_ID}
WHERE EQUIP_SEQ = #{EQUIP_SEQ}
    AND SEQ = #{SEQ}

mapper.delete:
DELETE FROM jmes.TBL_EQUIP_REPAIR
WHERE EQUIP_SEQ = #{EQUIP_SEQ}
    AND SEQ = #{SEQ}

mapper.update:
UPDATE TBL_CONTROL_PART
SET
    MCT_NOTE = #{MCT_NOTE},
    MCT_WORK_TYPE = #{MCT_WORK_TYPE}
WHERE
    CONTROL_SEQ = #{CONTROL_SEQ}
    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}
