mapper.select:
SELECT X.MCT_WORK_SEQ , X.IMG_GFILE_SEQ , X.CONTROL_SEQ , X.CONTROL_DETAIL_SEQ , jmes.SF_GET_CODE_NM('1010', X.PROCESS_TYPE, #{LOGIN_LOCALE}) AS PROCESS_TYPE_NM , X.EQUIP_NM , X.CONTROL_NUM , X.SIZE_TXT , jmes.SF_GET_CODE_NM('1035', X.MATERIAL_TYPE, #{LOGIN_LOCALE}) AS MATERIAL_TYPE_NM , jmes.SF_GET_USER_NM(X.WORK_USER_ID) AS WORK_USER_NM , jmes.SF_GET_DATE_F(X.WORK_START_DT,'','M') AS WORK_START_DT , jmes.SF_GET_DATE_F(X.WORK_FINISH_DT,'','M') AS WORK_FINISH_DT , X.FINISH_QTY , X.ERROR_QTY , CASE WHEN X.LAST_RETURN_SEQ > X.LAST_INSPECT_SEQ THEN (SELECT jmes.SF_GET_CODE_NM('1040', M.INSPECT_GRADE, #{LOGIN_LOCALE}) FROM jmes.TBL_INSPECT M WHERE M.INSPECT_SEQ = X.LAST_RETURN_SEQ ) ELSE (SELECT jmes.SF_GET_CODE_NM('1040', M.INSPECT_GRADE, #{LOGIN_LOCALE}) FROM jmes.TBL_INSPECT M WHERE M.INSPECT_SEQ = X.LAST_INSPECT_SEQ ) END AS LAST_INSPECT_NM FROM ( SELECT A.MCT_WORK_SEQ , A.CONTROL_SEQ , A.CONTROL_DETAIL_SEQ , D.PROCESS_TYPE , D.EQUIP_NM , CASE WHEN C.PART_NUM IS NULL THEN B.CONTROL_NUM ELSE CONCAT(B.CONTROL_NUM, ' #', C.PART_NUM) END AS CONTROL_NUM , C.SIZE_TXT , C.MATERIAL_TYPE , A.WORK_USER_ID , A.WORK_START_DT , A.WORK_FINISH_DT , A.FINISH_QTY , C.IMG_GFILE_SEQ , A.ERROR_QTY , (SELECT MAX(M.INSPECT_SEQ) FROM jmes.TBL_INSPECT M WHERE M.CONTROL_SEQ = A.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = A.CONTROL_DETAIL_SEQ AND M.INSPECT_TYPE = '1') AS LAST_INSPECT_SEQ , (SELECT MAX(M.INSPECT_SEQ) FROM jmes.TBL_INSPECT M WHERE M.CONTROL_SEQ = A.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = A.CONTROL_DETAIL_SEQ AND M.INSPECT_TYPE = '2' AND M.INSPECT_GRADE IN ('GRD050','GRD060')) AS LAST_RETURN_SEQ FROM jmes.TBL_MCT_WORK A , TBL_CONTROL B , TBL_CONTROL_PART C , jmes.TBL_EQUIP D WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_SEQ = C.CONTROL_SEQ AND A.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND A.EQUIP_SEQ = D.EQUIP_SEQ AND A.DEL_YN = 'N' AND A.WORK_FINISH_DT >= jmes.SF_GET_SHIFT_DAY_TIME('START', DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')) AND A.WORK_FINISH_DT < jmes.SF_GET_SHIFT_DAY_TIME('END', DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')) -- AND A.WORK_FINISH_DT <![CDATA[ < jmes.SF_GET_SHIFT_DAY_TIME('END', '20210129')
AND A.WORK_USER_ID = #{USER_ID}
AND A.EQUIP_SEQ IN ( SELECT M.EQUIP_SEQ FROM jmes.TBL_BATCH_MCT_WORK_EQUIP M WHERE M.BATCH_DT = DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')
AND M.WORK_USER_ID = #{USER_ID}
)
AND A.EQUIP_SEQ IN ( SELECT EQUIP_SEQ FROM TBL_EQUIP WHERE FACTORY_AREA = #{FACTORY_AREA} )
) X ORDER BY X.WORK_START_DT DESC, X.CONTROL_NUM

SELECT Y.CAM_SEQ , Y.CONTROL_SEQ , Y.CONTROL_DETAIL_SEQ , jmes.SF_GET_DATE_F(Y.FINISH_DT,'','M') AS FINISH_DT , jmes.SF_GET_DATE_F(Y.INNER_DUE_DT,'','') AS INNER_DUE_DT , Y.CONTROL_NUM , Y.SIZE_TXT , Y.SIZE_TXT , Y.PART_QTY , Y.STEP_SEQ , jmes.SF_GET_CODE_NM('1080', Y.WORK_DIRECTION, #{LOGIN_LOCALE}) AS WORK_DIRECTION_NM , jmes.SF_GET_CODE_NM('1010', Z.PROCESS_TYPE, #{LOGIN_LOCALE}) AS PROCESS_TYPE_NM , Z.EQUIP_NM FROM ( SELECT X.CAM_SEQ , X.CONTROL_SEQ , X.CONTROL_DETAIL_SEQ , X.FINISH_DT , X.INNER_DUE_DT , X.CONTROL_NUM , X.SIZE_TXT , X.PART_QTY , X.STEP_SEQ , X.WORK_DIRECTION , ( SELECT M.EQUIP_SEQ FROM jmes.TBL_MCT_WORK M WHERE M.MCT_WORK_SEQ = X.MCT_WORK_SEQ) AS EQUIP_SEQ FROM ( SELECT A.CAM_SEQ , A.CONTROL_SEQ , A.CONTROL_DETAIL_SEQ , A.FINISH_DT , C.INNER_DUE_DT , CASE WHEN C.PART_NUM IS NULL THEN B.CONTROL_NUM ELSE CONCAT(B.CONTROL_NUM, ' #', C.PART_NUM) END AS CONTROL_NUM , C.SIZE_TXT , jmes.SF_GET_CONTROL_PART_QTY(C.CONTROL_SEQ, C.CONTROL_DETAIL_SEQ) AS PART_QTY , D.SEQ AS STEP_SEQ , D.WORK_DIRECTION , (SELECT MAX(M.MCT_WORK_SEQ) FROM jmes.TBL_MCT_WORK M WHERE M.CONTROL_SEQ = C.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND M.DEL_YN = 'N' AND M.WORK_STATUS <> 'DBS040' ) AS MCT_WORK_SEQ -- 추가조건 확인(WORK_STATUS OR WORK_FINISH_DT...) FROM jmes.TBL_CAM A , TBL_CONTROL B , TBL_CONTROL_PART C , jmes.TBL_CAM_DETAIL D WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_SEQ = C.CONTROL_SEQ AND A.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND A.CAM_SEQ = D.CAM_SEQ AND A.FINISH_DT >= jmes.SF_GET_SHIFT_DAY_TIME('START', DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')) AND A.FINISH_DT < jmes.SF_GET_SHIFT_DAY_TIME('END', DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')) ) X ) Y, jmes.TBL_EQUIP Z WHERE Y.EQUIP_SEQ = Z.EQUIP_SEQ AND Y.EQUIP_SEQ IN ( SELECT M.EQUIP_SEQ FROM jmes.TBL_BATCH_MCT_WORK_EQUIP M WHERE M.BATCH_DT = DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')
AND M.WORK_USER_ID = #{USER_ID}
) ORDER BY Y.FINISH_DT DESC, Y.CONTROL_NUM, Y.STEP_SEQ

SELECT X.CONTROL_SEQ , X.CONTROL_DETAIL_SEQ , jmes.SF_GET_DATE_F(X.WORK_CONFIRM_DT, '', 'M') AS WORK_CONFIRM_DT , X.EMERGENCY_DELAY_NM , CASE WHEN IFNULL(X.NOT_SETTLEMENT_RETURN_QTY,0) > 0 THEN '반품' ELSE (SELECT CASE WHEN COUNT(*) > 0 THEN '불량' END FROM jmes.TBL_INSPECT M WHERE M.INSPECT_SEQ = X.INSPECT_SEQ AND M.INSPECT_GRADE = 'GRD040') END AS ERROR_RETURN_NM , jmes.SF_GET_DATE_F(X.INNER_DUE_DT,'','') AS INNER_DUE_DT , CASE WHEN DATE_FORMAT(X.INNER_DUE_DT,'%Y%m%d') < DATE_FORMAT(NOW(),'%Y%m%d') THEN 'Y' ELSE 'N' END AS DELAY_YN , X.WORK_FACTORY_NM , X.PART_STATUS_NM , X.ORDER_COMP_NM , X.CONTROL_NUM , X.WORK_TYPE_NM , X.PART_QTY , X.SIZE_TXT , ( SELECT jmes.SF_GET_CODE_NM('1009', M.POP_POSITION, #{LOGIN_LOCALE}) FROM TBL_POP M WHERE M.POP_SEQ = X.POP_SEQ) AS POP_POSITION_NM -- 언어조건 확인 , X.NOTE , X.WORK_STATUS_TYPE , X.ASSEMBLY_YN , X.IMG_GFILE_SEQ FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ , CASE WHEN B.PART_STATUS = 'PRO002' THEN B.STATUS_DT ELSE (SELECT MAX(M.STATUS_DT) FROM TBL_CONTROL_PART_PROGRESS M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND M.PART_STATUS = 'PRO002') END AS WORK_CONFIRM_DT , CASE WHEN A.CONTROL_STATUS = 'ORD005' THEN '보류' WHEN A.EMERGENCY_YN = 'Y' THEN '긴급' END AS EMERGENCY_DELAY_NM , ( SELECT COUNT(*) FROM jmes.TBL_INSPECT M WHERE M.INSPECT_TYPE = '2' AND M.INSPECT_GRADE = 'GRD050' AND M.RETURN_SETTLEMENT_DT IS NULL AND M.CONTROL_SEQ = A.CONTROL_SEQ ) AS NOT_SETTLEMENT_RETURN_QTY , B.INNER_DUE_DT , jmes.SF_GET_CODE_NM('1014',B.WORK_FACTORY,#{LOGIN_LOCALE}) AS WORK_FACTORY_NM , jmes.SF_GET_PART_STATUS_NM(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ, #{LOGIN_LOCALE}) AS PART_STATUS_NM , SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM , CASE WHEN B.PART_NUM IS NULL THEN A.CONTROL_NUM ELSE CONCAT(A.CONTROL_NUM, ' #', B.PART_NUM) END AS CONTROL_NUM , jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ) AS PART_QTY , jmes.SF_GET_CODE_NM('1033', B.WORK_TYPE, #{LOGIN_LOCALE}) AS WORK_TYPE_NM , B.SIZE_TXT , B.IMG_GFILE_SEQ , (SELECT MAX(M.INSPECT_SEQ) FROM jmes.TBL_INSPECT M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND M.INSPECT_TYPE = '1') AS INSPECT_SEQ , (SELECT MAX(POP_SEQ) FROM jmes.TBL_POP M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ) AS POP_SEQ , A.NOTE , CASE WHEN B.PART_STATUS IN ('PRO002','PRO004','PRO005','PRO021','PRO022','PRO017') THEN '1' WHEN B.PART_STATUS IN ('PRO006','PRO007','PRO008','PRO018') THEN '2' WHEN B.PART_STATUS IN ('PRO009','PRO012','PRO013','PRO014','PRO015') THEN '3' END WORK_STATUS_TYPE , CASE WHEN B.WORK_TYPE = 'WTP020' THEN 'Y' ELSE 'N' END AS ASSEMBLY_YN FROM TBL_CONTROL A, TBL_CONTROL_PART B WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_STATUS IN ('ORD001','ORD003','ORD005') AND A.DEL_YN = 'N' AND A.OUT_FINISH_DT IS NULL AND B.PART_STATUS IS NOT NULL AND B.PART_STATUS IN ( 'PRO002','PRO004','PRO005','PRO021','PRO022','PRO017' , 'PRO006','PRO007','PRO008','PRO018' , 'PRO009','PRO012','PRO013','PRO014','PRO015' )
AND B.WORK_FACTORY = #{WORK_FACTORY}
) X WHERE X.WORK_STATUS_TYPE = #{WORK_STATUS_TYPE}
AND X.ASSEMBLY_YN = 'N'
ORDER BY ISNULL(ERROR_RETURN_NM) ASC, X.INNER_DUE_DT

SELECT X.CONTROL_SEQ , X.CONTROL_DETAIL_SEQ , jmes.SF_GET_DATE_F(X.WORK_CONFIRM_DT, '', 'M') AS WORK_CONFIRM_DT , X.EMERGENCY_DELAY_NM , CASE WHEN X.OUT_FINISH_STATUS = 'RLS030' THEN '반품' ELSE (SELECT CASE WHEN COUNT(*) > 0 THEN '불량' END FROM jmes.TBL_INSPECT M WHERE M.INSPECT_SEQ = X.INSPECT_SEQ AND M.INSPECT_GRADE NOT IN ('GRD010','GRD020','GRD030')) END AS ERROR_RETURN_NM , jmes.SF_GET_DATE_F(X.INNER_DUE_DT,'','') AS INNER_DUE_DT , CASE WHEN DATE_FORMAT(X.INNER_DUE_DT,'%Y%m%d') < DATE_FORMAT(NOW(),'%Y%m%d') THEN 'Y' ELSE 'N' END AS DELAY_YN , X.WORK_FACTORY_NM , X.PART_STATUS_NM , X.ORDER_COMP_NM , X.CONTROL_NUM , X.WORK_TYPE_NM , X.PART_QTY , X.SIZE_TXT , ( SELECT jmes.SF_GET_CODE_NM('1009', M.POP_POSITION, #{LOGIN_LOCALE}) FROM TBL_POP M WHERE M.POP_SEQ = X.POP_SEQ) AS POP_POSITION_NM -- 언어조건 확인 , X.NOTE , X.ASSEMBLY_YN , X.IMG_GFILE_SEQ , X.OUT_FINISH_STATUS FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ, A.OUT_FINISH_STATUS , CASE WHEN B.PART_STATUS = 'PRO002' THEN B.STATUS_DT ELSE (SELECT MAX(M.STATUS_DT) FROM TBL_CONTROL_PART_PROGRESS M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND M.PART_STATUS = 'PRO002') END AS WORK_CONFIRM_DT , CASE WHEN A.CONTROL_STATUS = 'ORD005' THEN '보류' WHEN A.EMERGENCY_YN = 'Y' THEN '긴급' END AS EMERGENCY_DELAY_NM , ( SELECT COUNT(*) FROM jmes.TBL_INSPECT M WHERE M.INSPECT_TYPE = '2' AND M.INSPECT_GRADE = 'GRD050' AND M.RETURN_SETTLEMENT_DT IS NULL AND M.CONTROL_SEQ = A.CONTROL_SEQ ) AS NOT_SETTLEMENT_RETURN_QTY , B.INNER_DUE_DT , jmes.SF_GET_CODE_NM('1014',B.WORK_FACTORY,#{LOGIN_LOCALE}) AS WORK_FACTORY_NM , jmes.SF_GET_PART_STATUS_NM(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ, #{LOGIN_LOCALE}) AS PART_STATUS_NM , SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM , CASE WHEN B.PART_NUM IS NULL THEN A.CONTROL_NUM ELSE CONCAT(A.CONTROL_NUM, ' #', B.PART_NUM) END AS CONTROL_NUM , jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ) AS PART_QTY , jmes.SF_GET_CODE_NM('1033', B.WORK_TYPE, #{LOGIN_LOCALE}) AS WORK_TYPE_NM , B.SIZE_TXT , B.IMG_GFILE_SEQ , (SELECT MAX(M.INSPECT_SEQ) FROM jmes.TBL_INSPECT M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND M.INSPECT_TYPE = '1') AS INSPECT_SEQ , (SELECT MAX(POP_SEQ) FROM jmes.TBL_POP M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ) AS POP_SEQ , A.NOTE , CASE WHEN B.WORK_TYPE = 'WTP020' THEN 'Y' ELSE 'N' END AS ASSEMBLY_YN FROM TBL_CONTROL A, TBL_CONTROL_PART B WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_STATUS IN ('ORD001','ORD003','ORD005') AND A.DEL_YN = 'N' AND IFNULL( A.OUT_FINISH_STATUS,'RLS010') NOT IN ('RLS020') AND ( (A.OUT_FINISH_DT IS NULL
AND B.PART_STATUS IN ('PRO002','PRO004','PRO005','PRO021','PRO022','PRO017')
AND B.PART_STATUS IN ('PRO006','PRO007','PRO008','PRO018')
AND B.PART_STATUS IN ('PRO009','PRO012','PRO013','PRO014','PRO015')
) OR (A.OUT_FINISH_STATUS = 'RLS030') OR ( (A.CONTROL_SEQ IN ( SELECT CONTROL_SEQ FROM TBL_INSPECT M WHERE M.INSPECT_GRADE NOT IN ('GRD010','GRD020','GRD030') AND M.INSPECT_TYPE NOT IN (2) AND M.RETURN_SETTLEMENT_DT IS NULL AND M.CONTROL_SEQ = A.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND M.INSPECT_NUM = ( SELECT MAX(INSPECT_NUM) FROM TBL_INSPECT N WHERE M.CONTROL_SEQ = N.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = N.CONTROL_DETAIL_SEQ GROUP BY M.CONTROL_SEQ , M.CONTROL_DETAIL_SEQ ORDER BY M.CONTROL_SEQ ) ) ) AND A.OUT_FINISH_DT IS NULL ) )
AND B.WORK_FACTORY = #{WORK_FACTORY}
) X WHERE 1 = 1
AND X.ASSEMBLY_YN = 'N'
ORDER BY X.OUT_FINISH_STATUS DESC, ISNULL(ERROR_RETURN_NM) ASC, X.INNER_DUE_DT

SELECT BOARD_SEQ, GROUP_SEQ, GROUP_IN_NO, DEPTH, CASE WHEN BOARD_TYPE = 1 THEN '공지' ELSE '일반' END AS BOARD_TYPE, BOARD_TITLE, BOARD_CONTENTS, DATE_FORMAT(INSERT_DT, '%Y-%m-%d %H:%i') AS INSERT_DT, jmes.SF_GET_USER_NM(INSERT_ID) AS INSERT_ID FROM jmes.TBL_DASH_BOARD WHERE DEL_YN = 'N'
AND BOARD_TITLE LIKE CONCAT('%',#{SEARCH_TITLE},'%')
ORDER BY FIELD(BOARD_TYPE, 1, 2) ASC, GROUP_SEQ DESC LIMIT ${PAGE_NUM},${PAGE_SIZE}

SELECT MAX(GROUP_IN_NO + 1) AS GROUP_IN_NO, (#{DEPTH} + 1) AS DEPTH
FROM TBL_DASH_BOARD
WHERE DEL_YN = 'N'
  AND GROUP_SEQ  = #{GROUP_SEQ}
  AND DEPTH = #{DEPTH};

mapper.insert:
INSERT INTO TBL_DASH_BOARD(
   BOARD_SEQ
   ,GROUP_SEQ
   ,GROUP_IN_NO
   ,DEPTH
   ,BOARD_TYPE
   ,BOARD_TITLE
   ,BOARD_CONTENTS
   ,INSERT_ID

    ,FILE_SEQ_1


    ,FILE_SEQ_2


    ,FILE_SEQ_3

)
VALUES(
   NEXTVAL(jmes.SEQ_BOARD_NEW)


            ,#{GROUP_SEQ}


            ,NEXTVAL(jmes.SEQ_BOARD_GROUP)


   ,#{GROUP_IN_NO}
   ,#{DEPTH}
   ,#{BOARD_TYPE}
   ,#{BOARD_TITLE}
   ,#{BOARD_CONTENTS}
   ,#{LOGIN_USER_ID}

    ,#{FILE_SEQ_1}


    ,#{FILE_SEQ_2}


    ,#{FILE_SEQ_3}

)

mapper.update:
UPDATE TBL_DASH_BOARD
SET
    BOARD_TYPE = #{BOARD_TYPE},
    BOARD_TITLE = #{BOARD_TITLE},
    BOARD_CONTENTS = #{BOARD_CONTENTS},
    UPDATE_ID = #{LOGIN_USER_ID},
    UPDATE_DT = now()
    ,FILE_SEQ_1 = #{FILE_SEQ_1}
    ,FILE_SEQ_2 = #{FILE_SEQ_2}
    ,FILE_SEQ_3 = #{FILE_SEQ_3}
WHERE BOARD_SEQ = #{BOARD_SEQ}
