INSERT INTO TBL_CONTROL_PART ( CONTROL_SEQ, CONTROL_DETAIL_SEQ, PART_NUM, DRAWING_NUM, DRAWING_VER, DRAWING_UP_DT, PREV_DRAWING_NUM, WORK_TYPE, OUTSIDE_YN, WORK_FACTORY, MATERIAL_SUPPLY_YN, INNER_DUE_DT, SIZE_TXT, SIZE_TYPE, SIZE_W, SIZE_H, SIZE_T, SIZE_D, SIZE_L, SIZE_W_M, SIZE_H_M, SIZE_T_M, SIZE_D_M, SIZE_L_M, MATERIAL_TYPE, MATERIAL_DETAIL, MATERIAL_KIND, SURFACE_TREAT, MATERIAL_NOTE, PART_UNIT_QTY, MATERIAL_FINISH_GRIND, MATERIAL_FINISH_HEAT, UNIT_MATERIAL_AMT, UNIT_MATERIAL_FINISH_GRIND_AMT, UNIT_MATERIAL_FINISH_HEAT_AMT, UNIT_SURFACE_AMT, UNIT_PROCESS_AMT, UNIT_ETC_AMT, UNIT_AMT_NOTE, DWG_GFILE_SEQ, DXF_GFILE_SEQ, PDF_GFILE_SEQ, IMG_GFILE_SEQ, PART_STATUS, STATUS_DT, MCT_NOTE, MCT_WORK_TYPE, OUTSIDE_COMP_CD, OUTSIDE_ORDER_NUM, OUTSIDE_NOTE, OUTSIDE_MATERIAL_SUPPLY_YN, OUTSIDE_REQUEST_FINISH_YN, OUTSIDE_REQUEST_PROCESS_YN, OUTSIDE_REQUEST_GRIND_YN, OUTSIDE_REQUEST_SURFACE_YN, OUTSIDE_REQUEST_ETC, OUTSIDE_HOPE_DUE_DT, OUTSIDE_UNIT_AMT, OUTSIDE_IN_DT, OUTSIDE_STATUS, OUTSIDE_STATUS_DT, INSERT_ID ) SELECT #{CONTROL_SEQ}, NEXTVAL(SEQ_CONTROL_PART), #{PART_NUM}, #{ORDER_DRAWING_NUM}, #{DRAWING_VER}, #{DRAWING_UP_DT}, #{PREV_DRAWING_NUM}, #{WORK_TYPE}, #{OUTSIDE_YN}, #{WORK_FACTORY}, #{MATERIAL_SUPPLY_YN}, DATE_FORMAT(#{INNER_DUE_DT}, '%Y%m%d'), #{SIZE_TXT}, SF_GET_SIZE_TXT('SIZE_TYPE', #{SIZE_TXT}), SF_GET_SIZE_TXT('SIZE_W', #{SIZE_TXT}), SF_GET_SIZE_TXT('SIZE_H', #{SIZE_TXT}), SF_GET_SIZE_TXT('SIZE_T', #{SIZE_TXT}), SF_GET_SIZE_TXT('SIZE_D', #{SIZE_TXT}), SF_GET_SIZE_TXT('SIZE_L', #{SIZE_TXT}), #{SIZE_W_M}, #{SIZE_H_M}, #{SIZE_T_M}, #{SIZE_D_M}, #{SIZE_L_M}, SF_GET_ETC_CD('1027', #{MATERIAL_DETAIL}), #{MATERIAL_DETAIL}, #{MATERIAL_KIND}, #{SURFACE_TREAT}, #{MATERIAL_NOTE}, #{PART_UNIT_QTY}, #{MATERIAL_FINISH_GRIND}, #{MATERIAL_FINISH_HEAT}, REPLACE(#{UNIT_MATERIAL_AMT}, ',', SPACE(0)), REPLACE(#{UNIT_MATERIAL_FINISH_GRIND_AMT}, ',', SPACE(0)), REPLACE(#{UNIT_MATERIAL_FINISH_HEAT_AMT}, ',', SPACE(0)), REPLACE(#{UNIT_SURFACE_AMT}, ',', SPACE(0)), REPLACE(#{UNIT_PROC