INSERT INTO TBL_CONTROL_PART_ORDER ( CONTROL_SEQ, CONTROL_DETAIL_SEQ, ORDER_SEQ, ORDER_NUM, ORDER_QTY, ORDER_DUE_DT, DELIVERY_DT, PROJECT_NM, MODULE_NM, DELIVERY_COMP_NM, LABEL_NOTE, ORDER_STAFF_SEQ, DESIGNER_NM, DRAWING_NUM, DWG_GFILE_SEQ, DXF_GFILE_SEQ, PDF_GFILE_SEQ, IMG_GFILE_SEQ, VIEW_GFILE_SEQ, ITEM_NM, ORIGINAL_SIDE_QTY, OTHER_SIDE_QTY, UNIT_FINAL_EST_AMT, UNIT_FINAL_AMT, ORDER_BARCODE_NUM, INSERT_ID ) SELECT #{CONTROL_SEQ}, IFNULL(#{CONTROL_DETAIL_SEQ}, ( SELECT M.CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART M WHERE M.CONTROL_SEQ = #{CONTROL_SEQ} AND M.PART_NUM = #{PART_NUM})), NEXTVAL(SEQ_CONTROL_PART_ORDER), #{ORDER_NUM}, #{ORDER_QTY}, DATE_FORMAT(#{ORDER_DUE_DT}, '%Y%m%d'), DATE_FORMAT(#{DELIVERY_DT}, '%Y%m%d'), #{PROJECT_NM}, #{MODULE_NM}, #{DELIVERY_COMP_NM}, #{LABEL_NOTE}, #{ORDER_STAFF_SEQ}, #{DESIGNER_NM}, #{ORDER_DRAWING_NUM}, #{DWG_GFILE_SEQ}, #{DXF_GFILE_SEQ}, #{PDF_GFILE_SEQ}, #{IMG_GFILE_SEQ}, #{VIEW_GFILE_SEQ}, #{ITEM_NM}, REPLACE(#{ORIGINAL_SIDE_QTY}, ',', SPACE(0)), REPLACE(#{OTHER_SIDE_QTY}, ',', SPACE(0)), REPLACE(#{UNIT_FINAL_EST_AMT}, ',', SPACE(0)), REPLACE(#{UNIT_FINAL_AMT}, ',', SPACE(0)), CONCAT('O',LPAD(NEXTVAL(SEQ_ORDER_BARCODE),9,'0')), #{LOGIN_USER_ID} FROM DUAL WHERE NOT EXISTS ( SELECT * FROM TBL_CONTROL_PART_ORDER M WHERE M.CONTROL_SEQ = #{CONTROL_SEQ} AND M.ORDER_NUM = #{ORDER_NUM}
AND M.DRAWING_NUM = #{ORDER_DRAWING_NUM}
)

mapper.select:
SELECT ROW_NUMBER () OVER () AS ROW_NUM, , , SF_GET_DATE_F(C.ORDER_DUE_DT, '', '') AS ORDER_DUE_DT FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON B.CONTROL_SEQ = A.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER_MAIN C ON C.CONTROL_SEQ = A.CONTROL_SEQ WHERE A.DEL_YN = 'N' AND A.CONTROL_STATUS IN ('ORD001', 'ORD005') AND A.OUT_FINISH_DT IS NULL AND IFNULL(B.OUTSIDE_YN, 'N') = 'N' AND (IFNULL(B.PART_STATUS, 'PRO003') = 'PRO003' OR A.CONTROL_STATUS IN ('ORD005'))
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.MATERIAL_DETAIL = #{MATERIAL_DETAIL}
ORDER BY A.STATUS_DT DESC, A.CONTROL_SEQ, B.PART_NUM

SELECT NOT EXISTS ( SELECT *
                    FROM TBL_CONTROL
                    WHERE 1 = 1
                        AND CONTROL_SEQ = #{CONTROL_SEQ}
                        AND (CONTROL_STATUS IS NULL OR CONTROL_STATUS = 'ORD002')
                  ) AS FLAG

SELECT EXISTS ( SELECT *
                FROM TBL_CONTROL_PART
                WHERE 1 = 1
                    AND CONTROL_SEQ = #{CONTROL_SEQ}
                    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}
                    AND OUTSIDE_CONFIRM_DT IS NOT NULL
              ) AS FLAG

SELECT EXISTS ( SELECT *
                FROM TBL_MATERIAL_ORDER
                WHERE 1 = 1
                    AND CONTROL_SEQ = #{CONTROL_SEQ}
                    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}
                    AND ORDER_STATUS IN ('MST001', 'MST002', 'MST004')
              ) AS FLAG

SELECT EXISTS ( SELECT *
                FROM TBL_MCT_WORK
                WHERE 1 = 1
                    AND CONTROL_SEQ = #{CONTROL_SEQ}
                    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}
              ) AS FLAG

SELECT EXISTS ( SELECT *
                FROM TBL_CAM
                WHERE 1 = 1
                    AND CONTROL_SEQ = #{CONTROL_SEQ}
                    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}
              ) AS FLAG

SELECT EXISTS ( SELECT *
                FROM TBL_INSPECT
                WHERE 1 = 1
                    AND CONTROL_SEQ =  #{CONTROL_SEQ}
                    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}
              ) AS FLAG

