      AND CONTROL_DETAIL_SEQ  = #{CONTROL_DETAIL_SEQ}

mapper.select:
SELECT ROW_NUMBER () OVER (ORDER BY A.CONTROL_SEQ, B.PART_NUM) AS ROW_NUM, , , SF_GET_DATE_F(C.ORDER_DUE_DT, '', '') AS ORDER_DUE_DT, SF_GET_CODE_NM('1009', D.POP_POSITION, #{LOGIN_LOCALE}) AS LAST_POP_POSITION FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER_MAIN C ON C.CONTROL_SEQ = A.CONTROL_SEQ LEFT OUTER JOIN TBL_POP D ON B.CONTROL_SEQ = D.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = D.CONTROL_DETAIL_SEQ AND D.POP_SEQ = (SELECT MAX(X.POP_SEQ) FROM TBL_POP X WHERE X.CONTROL_SEQ = D.CONTROL_SEQ AND X.CONTROL_DETAIL_SEQ = D.CONTROL_DETAIL_SEQ) WHERE A.DEL_YN = 'N' AND A.CONTROL_STATUS = 'ORD001' AND A.OUT_FINISH_DT IS NULL AND IFNULL(B.OUTSIDE_YN, 'N') = 'N' AND IFNULL(B.PART_STATUS, 'PRO003') != 'PRO003' AND B.INNER_WORK_FINISH_DT IS NULL
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND A.EMERGENCY_YN = 'Y'
AND B.MATERIAL_DETAIL = #{MATERIAL_DETAIL}
ORDER BY A.STATUS_DT DESC, A.CONTROL_SEQ, B.PART_NUM

SELECT ROW_NUMBER () OVER (ORDER BY A.CONTROL_SEQ, B.PART_NUM) AS ROW_NUM, , , SF_GET_DATE_F(C.ORDER_DUE_DT, '', '') AS ORDER_DUE_DT FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON B.CONTROL_SEQ = A.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER_MAIN C ON C.CONTROL_SEQ = A.CONTROL_SEQ WHERE A.DEL_YN = 'N' AND A.CONTROL_STATUS = 'ORD001' AND A.OUT_FINISH_DT IS NULL AND B.OUTSIDE_YN = 'Y' AND B.OUTSIDE_IN_DT IS NULL
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.OUTSIDE_COMP_CD = #{OUTSIDE_COMP_CD}
ORDER BY A.STATUS_DT DESC, A.CONTROL_SEQ, B.PART_NUM

