mapper.select:
SELECT X.GROUP_KEY , ROW_NUMBER () OVER(ORDER BY X.COMP_NM, X.`YEAR`, X.QUARTER, X.FINISH_MONTH, X.ORDER_COMP_NM, X.CLOSE_VER) AS ROW_NUM , X.COMP_CD , X.COMP_NM , X.CLOSE_MONTH , X.`YEAR` , X.QUARTER , X.FINISH_MONTH , X.CLOSE_VER , X.CLOSE_VER_TRAN , X.ORDER_COMP_CD , X.ORDER_COMP_NM , COUNT(*) AS ITEM_NUMBER , SUM(X.TOTAL_AMT) AS TOTAL_AMT , X.FINAL_NEGO_AMT , X.VAT_AMOUNT , X.TOTAL_AMOUNT , X.CLOSE_NOTE FROM ( SELECT 1 AS GROUP_KEY , A.COMP_CD , SF_GET_COMP_NM(A.COMP_CD) AS COMP_NM , D.CLOSE_MONTH , SUBSTR(D.CLOSE_MONTH, 1, 4) AS `YEAR` , CONCAT(QUARTER(STR_TO_DATE(D.CLOSE_MONTH, '%Y%m')), '분기') AS QUARTER , CONCAT(SUBSTR(D.CLOSE_MONTH, 5), '월') AS FINISH_MONTH , D.CLOSE_VER , CONCAT(D.CLOSE_VER, '차') AS CLOSE_VER_TRAN , A.ORDER_COMP_CD , SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM , C.UNIT_FINAL_AMT * C.ORDER_QTY AS TOTAL_AMT , E.FINAL_NEGO_AMT , E.FINAL_NEGO_AMT * 0.1 AS VAT_AMOUNT , E.FINAL_NEGO_AMT + E.FINAL_NEGO_AMT * 0.1 AS TOTAL_AMOUNT , E.CLOSE_NOTE FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON B.CONTROL_SEQ = A.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER C ON C.CONTROL_SEQ = B.CONTROL_SEQ AND C.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ INNER JOIN TBL_MONTH_CLOSE_ORDER D ON D.CONTROL_SEQ = A.CONTROL_SEQ AND D.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND D.ORDER_SEQ = C.ORDER_SEQ AND D.COMP_CD = A.COMP_CD AND D.ORDER_COMP_CD = A.ORDER_COMP_CD INNER JOIN TBL_MONTH_CLOSE E ON E.CLOSE_MONTH = D.CLOSE_MONTH AND E.CLOSE_VER = D.CLOSE_VER AND E.COMP_CD = D.COMP_CD AND E.ORDER_COMP_CD = D.ORDER_COMP_CD WHERE A.DEL_YN = 'N'
AND (D.CLOSE_MONTH BETWEEN DATE_FORMAT(STR_TO_DATE(CONCAT(#{CLOSE_YEAR_LEFT}, #{CLOSE_MONTH_LEFT}), '%Y%m'), '%Y%m') AND DATE_FORMAT(STR_TO_DATE(CONCAT(#{CLOSE_YEAR_RIGHT}, #{CLOSE_MONTH_RIGHT}), '%Y%m'), '%Y%m'))
AND D.CLOSE_MONTH = DATE_FORMAT(STR_TO_DATE(CONCAT(#{CLOSE_YEAR_LEFT}, #{CLOSE_MONTH_LEFT}), '%Y%m'), '%Y%m')
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND D.CLOSE_VER = #{CLOSE_VER}
AND UPPER(A.NOTE) LIKE CONCAT('%', UPPER(#{NOTE}), '%')
) X GROUP BY X.COMP_CD, X.ORDER_COMP_CD, X.CLOSE_MONTH, X.CLOSE_VER ORDER BY X.COMP_NM, X.`YEAR`, X.QUARTER, X.FINISH_MONTH, X.ORDER_COMP_NM, X.CLOSE_VER

SELECT ROW_NUMBER () OVER (ORDER BY K.YYYY, K.COMP_CD, K.ORDER_COMP_CD, K.`TYPE`) AS ROW_NUM , K.COMP_CD , K.COMP_NM , K.ORDER_COMP_CD , K.ORDER_COMP_NM , K.YYYY , K.MM , CASE WHEN K.`TYPE` = '1' THEN '입금' ELSE '매출' END AS `TYPE` , K.CLOSE_MONTH , SUM(K.AMT_01) AS AMT_01 , SUM(K.AMT_02) AS AMT_02 , SUM(K.AMT_03) AS AMT_03 , SUM(K.Q1_SUM) AS Q1_SUM , SUM(K.AMT_04) AS AMT_04 , SUM(K.AMT_05) AS AMT_05 , SUM(K.AMT_06) AS AMT_06 , SUM(K.Q2_SUM) AS Q2_SUM , SUM(K.AMT_07) AS AMT_07 , SUM(K.AMT_08) AS AMT_08 , SUM(K.AMT_09) AS AMT_09 , SUM(K.Q3_SUM) AS Q3_SUM , SUM(K.AMT_10) AS AMT_10 , SUM(K.AMT_11) AS AMT_11 , SUM(K.AMT_12) AS AMT_12 , SUM(K.Q4_SUM) AS Q4_SUM , SUM(K.TOTAL_AMT) AS TOTAL_AMT FROM ( SELECT F.COMP_CD , F.COMP_NM , F.ORDER_COMP_CD , F.ORDER_COMP_NM , G.YYYY , G.MM , G.`TYPE` , H.CLOSE_MONTH , CASE WHEN G.MM = '01' THEN H.AMT ELSE 0 END AS AMT_01 , CASE WHEN G.MM = '02' THEN H.AMT ELSE 0 END AS AMT_02 , CASE WHEN G.MM = '03' THEN H.AMT ELSE 0 END AS AMT_03 , CASE WHEN G.MM IN ('01', '02', '03') THEN H.AMT ELSE 0 END AS Q1_SUM , CASE WHEN G.MM = '04' THEN H.AMT ELSE 0 END AS AMT_04 , CASE WHEN G.MM = '05' THEN H.AMT ELSE 0 END AS AMT_05 , CASE WHEN G.MM = '06' THEN H.AMT ELSE 0 END AS AMT_06 , CASE WHEN G.MM IN ('04', '05', '06') THEN H.AMT ELSE 0 END AS Q2_SUM , CASE WHEN G.MM = '07' THEN H.AMT ELSE 0 END AS AMT_07 , CASE WHEN G.MM = '08' THEN H.AMT ELSE 0 END AS AMT_08 , CASE WHEN G.MM = '09' THEN H.AMT ELSE 0 END AS AMT_09 , CASE WHEN G.MM IN ('07', '08', '09') THEN H.AMT ELSE 0 END AS Q3_SUM , CASE WHEN G.MM = '10' THEN H.AMT ELSE 0 END AS AMT_10 , CASE WHEN G.MM = '11' THEN H.AMT ELSE 0 END AS AMT_11 , CASE WHEN G.MM = '12' THEN H.AMT ELSE 0 END AS AMT_12 , CASE WHEN G.MM IN ('10', '11', '12') THEN H.AMT ELSE 0 END AS Q4_SUM , IFNULL(H.AMT, 0) AS TOTAL_AMT FROM ( SELECT A.COMP_CD , A.COMP_NM , B.COMP_CD AS ORDER_COMP_CD , B.COMP_NM AS ORDER_COMP_NM FROM ( SELECT COMP_CD , COMP_NM FROM TBL_COMPANY WHERE ACTIVE_YN = 'Y' AND FAMILY_COMPANY_YN = 'Y' ) A CROSS JOIN ( SELECT COMP_CD , COMP_NM FROM TBL_COMPANY WHERE ACTIVE_YN = 'Y' AND ORDER_COMPANY_YN = 'Y' ) B ) F CROSS JOIN ( SELECT '1' AS `TYPE` , YYYY , MM FROM TBL_YYYYMMDD WHERE YYYY = #{MONTH_SALE_YEAR} GROUP BY YYYY, MM UNION ALL SELECT 2 AS `TYPE` , YYYY , MM FROM TBL_YYYYMMDD WHERE YYYY = #{MONTH_SALE_YEAR} GROUP BY YYYY, MM ) G LEFT OUTER JOIN ( SELECT '1' AS `TYPE` , COMP_CD , ORDER_COMP_CD , DATE_FORMAT(DEPOSIT_DT, '%Y%m') AS CLOSE_MONTH , DEPOSIT_AMT AS AMT FROM TBL_MONEY_DEPOSIT WHERE SUBSTR(DEPOSIT_DT, 1, 4) = #{MONTH_SALE_YEAR}
AND COMP_CD = #{COMP_CD}
AND ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY COMP_CD, ORDER_COMP_CD, DATE_FORMAT(DEPOSIT_DT, '%Y%m') UNION ALL SELECT '2' AS `TYPE` , F.COMP_CD , F.ORDER_COMP_CD , F.CLOSE_MONTH , SUM(F.AMT) AS AMT FROM ( SELECT A.COMP_CD , A.ORDER_COMP_CD , D.CLOSE_MONTH , E.FINAL_NEGO_AMT + E.FINAL_NEGO_AMT * 0.1 AS AMT FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON B.CONTROL_SEQ = A.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER C ON C.CONTROL_SEQ = B.CONTROL_SEQ AND C.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND IFNULL(B.PART_NUM, '0') = '0' INNER JOIN TBL_MONTH_CLOSE_ORDER D ON D.CONTROL_SEQ = A.CONTROL_SEQ AND D.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND D.ORDER_SEQ = C.ORDER_SEQ AND D.COMP_CD = A.COMP_CD AND D.ORDER_COMP_CD = A.ORDER_COMP_CD INNER JOIN TBL_MONTH_CLOSE E ON E.CLOSE_MONTH = D.CLOSE_MONTH AND E.CLOSE_VER = D.CLOSE_VER AND E.COMP_CD = D.COMP_CD AND E.ORDER_COMP_CD = D.ORDER_COMP_CD AND SUBSTR(E.CLOSE_MONTH, 1, 4) = #{MONTH_SALE_YEAR} WHERE A.DEL_YN = 'N'
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY A.COMP_CD, A.ORDER_COMP_CD, D.CLOSE_MONTH, D.CLOSE_VER ) F GROUP BY F.COMP_CD, F.ORDER_COMP_CD, F.CLOSE_MONTH ) H ON H.COMP_CD = F.COMP_CD AND H.ORDER_COMP_CD = F.ORDER_COMP_CD AND H.CLOSE_MONTH = CONCAT(G.YYYY, G.MM) AND H.`TYPE` = G.`TYPE` GROUP BY G.YYYY, G.MM, F.COMP_CD, F.ORDER_COMP_CD, G.`TYPE` ) K WHERE 1 = 1
AND K.`TYPE` = '2'
AND K.COMP_CD = #{COMP_CD}
AND K.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY K.YYYY, K.COMP_CD, K.ORDER_COMP_CD, K.`TYPE` ORDER BY K.YYYY, K.COMP_CD, K.ORDER_COMP_CD, K.`TYPE`

SELECT NULL AS ROW_NUM , NULL AS COMP_CD , NULL AS COMP_NM , NULL AS ORDER_COMP_CD , NULL AS ORDER_COMP_NM , K.YYYY , K.MM , CASE WHEN K.`TYPE` = '1' THEN '입금' ELSE '매출' END AS `TYPE` , K.CLOSE_MONTH , SUM(K.AMT_01) AS AMT_01 , SUM(K.AMT_02) AS AMT_02 , SUM(K.AMT_03) AS AMT_03 , SUM(K.Q1_SUM) AS Q1_SUM , SUM(K.AMT_04) AS AMT_04 , SUM(K.AMT_05) AS AMT_05 , SUM(K.AMT_06) AS AMT_06 , SUM(K.Q2_SUM) AS Q2_SUM , SUM(K.AMT_07) AS AMT_07 , SUM(K.AMT_08) AS AMT_08 , SUM(K.AMT_09) AS AMT_09 , SUM(K.Q3_SUM) AS Q3_SUM , SUM(K.AMT_10) AS AMT_10 , SUM(K.AMT_11) AS AMT_11 , SUM(K.AMT_12) AS AMT_12 , SUM(K.Q4_SUM) AS Q4_SUM , SUM(K.TOTAL_AMT) AS TOTAL_AMT FROM ( SELECT F.COMP_CD , F.COMP_NM , F.ORDER_COMP_CD , F.ORDER_COMP_NM , G.YYYY , G.MM , G.`TYPE` , H.CLOSE_MONTH , CASE WHEN G.MM = '01' THEN H.AMT ELSE 0 END AS AMT_01 , CASE WHEN G.MM = '02' THEN H.AMT ELSE 0 END AS AMT_02 , CASE WHEN G.MM = '03' THEN H.AMT ELSE 0 END AS AMT_03 , CASE WHEN G.MM IN ('01', '02', '03') THEN H.AMT ELSE 0 END AS Q1_SUM , CASE WHEN G.MM = '04' THEN H.AMT ELSE 0 END AS AMT_04 , CASE WHEN G.MM = '05' THEN H.AMT ELSE 0 END AS AMT_05 , CASE WHEN G.MM = '06' THEN H.AMT ELSE 0 END AS AMT_06 , CASE WHEN G.MM IN ('04', '05', '06') THEN H.AMT ELSE 0 END AS Q2_SUM , CASE WHEN G.MM = '07' THEN H.AMT ELSE 0 END AS AMT_07 , CASE WHEN G.MM = '08' THEN H.AMT ELSE 0 END AS AMT_08 , CASE WHEN G.MM = '09' THEN H.AMT ELSE 0 END AS AMT_09 , CASE WHEN G.MM IN ('07', '08', '09') THEN H.AMT ELSE 0 END AS Q3_SUM , CASE WHEN G.MM = '10' THEN H.AMT ELSE 0 END AS AMT_10 , CASE WHEN G.MM = '11' THEN H.AMT ELSE 0 END AS AMT_11 , CASE WHEN G.MM = '12' THEN H.AMT ELSE 0 END AS AMT_12 , CASE WHEN G.MM IN ('10', '11', '12') THEN H.AMT ELSE 0 END AS Q4_SUM , IFNULL(H.AMT, 0) AS TOTAL_AMT FROM ( SELECT A.COMP_CD , A.COMP_NM , B.COMP_CD AS ORDER_COMP_CD , B.COMP_NM AS ORDER_COMP_NM FROM ( SELECT COMP_CD , COMP_NM FROM TBL_COMPANY WHERE ACTIVE_YN = 'Y' AND FAMILY_COMPANY_YN = 'Y' ) A CROSS JOIN ( SELECT COMP_CD , COMP_NM FROM TBL_COMPANY WHERE ACTIVE_YN = 'Y' AND ORDER_COMPANY_YN = 'Y' ) B ) F CROSS JOIN ( SELECT '1' AS `TYPE` , YYYY , MM FROM TBL_YYYYMMDD WHERE YYYY = #{MONTH_SALE_YEAR} GROUP BY YYYY, MM UNION ALL SELECT 2 AS `TYPE` , YYYY , MM FROM TBL_YYYYMMDD WHERE YYYY = #{MONTH_SALE_YEAR} GROUP BY YYYY, MM ) G LEFT OUTER JOIN ( SELECT '1' AS `TYPE` , COMP_CD , ORDER_COMP_CD , DATE_FORMAT(DEPOSIT_DT, '%Y%m') AS CLOSE_MONTH , DEPOSIT_AMT AS AMT FROM TBL_MONEY_DEPOSIT WHERE SUBSTR(DEPOSIT_DT, 1, 4) = #{MONTH_SALE_YEAR}
AND COMP_CD = #{COMP_CD}
AND ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY COMP_CD, ORDER_COMP_CD, DATE_FORMAT(DEPOSIT_DT, '%Y%m') UNION ALL SELECT '2' AS `TYPE` , F.COMP_CD , F.ORDER_COMP_CD , F.CLOSE_MONTH , SUM(F.AMT) AS AMT FROM ( SELECT A.COMP_CD , A.ORDER_COMP_CD , D.CLOSE_MONTH , E.FINAL_NEGO_AMT + E.FINAL_NEGO_AMT * 0.1 AS AMT FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON B.CONTROL_SEQ = A.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER C ON C.CONTROL_SEQ = B.CONTROL_SEQ AND C.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND IFNULL(B.PART_NUM, '0') = '0' INNER JOIN TBL_MONTH_CLOSE_ORDER D ON D.CONTROL_SEQ = A.CONTROL_SEQ AND D.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND D.ORDER_SEQ = C.ORDER_SEQ AND D.COMP_CD = A.COMP_CD AND D.ORDER_COMP_CD = A.ORDER_COMP_CD INNER JOIN TBL_MONTH_CLOSE E ON E.CLOSE_MONTH = D.CLOSE_MONTH AND E.CLOSE_VER = D.CLOSE_VER AND E.COMP_CD = D.COMP_CD AND E.ORDER_COMP_CD = D.ORDER_COMP_CD AND SUBSTR(E.CLOSE_MONTH, 1, 4) = #{MONTH_SALE_YEAR} WHERE A.DEL_YN = 'N'
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY A.COMP_CD, A.ORDER_COMP_CD, D.CLOSE_MONTH, D.CLOSE_VER ) F GROUP BY F.COMP_CD, F.ORDER_COMP_CD, F.CLOSE_MONTH ) H ON H.COMP_CD = F.COMP_CD AND H.ORDER_COMP_CD = F.ORDER_COMP_CD AND H.CLOSE_MONTH = CONCAT(G.YYYY, G.MM) AND H.`TYPE` = G.`TYPE` GROUP BY G.YYYY, G.MM, F.COMP_CD, F.ORDER_COMP_CD, G.`TYPE` ) K WHERE 1 = 1
AND K.`TYPE` = '2'
AND K.COMP_CD = #{COMP_CD}
AND K.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY K.YYYY, K.`TYPE` ORDER BY K.YYYY, K.`TYPE`

mapper.insert:
INSERT INTO TBL_CONTROL_BARCODE ( BARCODE_NUM, CONTROL_SEQ, CONTROL_DETAIL_SEQ, CONTROL_VER, DRAWING_VER) SELECT CONCAT('C',LPAD(NEXTVAL(SEQ_BARCODE),9,'0')) AS BARCODE_NUM, B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ, A.CONTROL_VER, B.DRAWING_VER FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ WHERE 1 = 1 AND A.DEL_YN = 'N' AND A.CONTROL_SEQ = #{CONTROL_SEQ} AND B.CONTROL_DETAIL_SEQ = (SELECT Q.CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART Q WHERE 1 = 1 AND Q.CONTROL_SEQ = #{CONTROL_SEQ}
AND Q.PART_NUM = #{PART_NUM}
AND Q.PART_NUM IS NULL
) AND NOT EXISTS (SELECT * FROM TBL_CONTROL_BARCODE X WHERE 1 = 1 AND X.CONTROL_SEQ = #{CONTROL_SEQ} AND X.CONTROL_DETAIL_SEQ = ( SELECT Q.CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART Q WHERE 1 = 1 AND Q.CONTROL_SEQ = #{CONTROL_SEQ}
AND Q.PART_NUM = #{PART_NUM}
AND Q.PART_NUM IS NULL
) AND X.DEL_YN = 'N')

mapper.select:
SELECT EXISTS ( SELECT * FROM TBL_INVOICE_DETAIL
                WHERE 1 = 1
                    AND CONTROL_SEQ = #{CONTROL_SEQ}
                	AND ORDER_SEQ = #{ORDER_SEQ}
                    AND INVOICE_NUM != #{INVOICE_NUM} ) AS FLAG

SELECT 1 AS GROUP_KEY, ROW_NUMBER() OVER(ORDER BY Z.COMP_CD, Z.ORDER_COMP_CD) AS ROW_NUM, Z.COMP_CD, (SELECT A.COMP_NM FORM FROM jmes.TBL_COMPANY A WHERE A.COMP_CD = Z.COMP_CD) AS COMP_CD_NM, Z.ORDER_COMP_CD, (SELECT A.COMP_NM FORM FROM jmes.TBL_COMPANY A WHERE A.COMP_CD = Z.ORDER_COMP_CD) AS ORDER_COMP_NM, Z.CLOSE_YEAR, SUM(Z.SALE_AMT) AS SALE_AMT, SUM(Z.CASH_AMT) AS CASH_AMT, SUM(Z.PAPER_AMT) AS PAPER_AMT, SUM(Z.DEPOSIT_TOTAL_AMT) AS DEPOSIT_TOTAL_AMT, SUM(Z.NOT_DEPOSIT_AMT) AS NOT_DEPOSIT_AMT, (SUM(Z.SALE_AMT) + SUM(Z.NOT_DEPOSIT_AMT)) - SUM(Z.DEPOSIT_TOTAL_AMT) AS NOT_DEPOSIT_TOTAL_AMT, Y.NOTE FROM ( SELECT Z.COMP_CD, Z.ORDER_COMP_CD, Z.CLOSE_YEAR, SUM(Z.SALE_AMT) AS SALE_AMT, 0 AS CASH_AMT, 0 AS PAPER_AMT, 0 AS DEPOSIT_TOTAL_AMT, 0 AS NOT_DEPOSIT_AMT FROM ( SELECT A.COMP_CD, A.ORDER_COMP_CD, DATE_FORMAT(STR_TO_DATE(D.CLOSE_MONTH, '%Y%m'), '%Y') as CLOSE_YEAR, E.FINAL_NEGO_AMT + E.FINAL_NEGO_AMT * 0.1 AS SALE_AMT FROM jmes.TBL_CONTROL A INNER JOIN jmes.TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ AND IFNULL(B.PART_NUM, '0') = '0' INNER JOIN jmes.TBL_CONTROL_PART_ORDER C ON B.CONTROL_SEQ = C.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ INNER JOIN jmes.TBL_MONTH_CLOSE_ORDER D ON C.CONTROL_SEQ = D.CONTROL_SEQ AND C.CONTROL_DETAIL_SEQ = D.CONTROL_DETAIL_SEQ AND C.ORDER_SEQ = D.ORDER_SEQ AND A.COMP_CD = D.COMP_CD AND A.ORDER_COMP_CD = D.ORDER_COMP_CD INNER JOIN jmes.TBL_MONTH_CLOSE E ON D.COMP_CD = E.COMP_CD AND D.CLOSE_MONTH = E.CLOSE_MONTH AND D.ORDER_COMP_CD = E.ORDER_COMP_CD AND D.CLOSE_VER = E.CLOSE_VER AND DATE_FORMAT(STR_TO_DATE(D.CLOSE_MONTH, '%Y%m'), '%Y') = #{MONEY_MANAGE_STATUS_YEAR} WHERE A.DEL_YN = 'N'
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY A.COMP_CD, A.ORDER_COMP_CD, D.CLOSE_MONTH, D.CLOSE_VER ) Z GROUP BY Z.COMP_CD, Z.ORDER_COMP_CD, Z.CLOSE_YEAR UNION ALL
SELECT A.COMP_CD, A.ORDER_COMP_CD, DATE_FORMAT(STR_TO_DATE(A.DEPOSIT_DT, '%Y%m%d'), '%Y') as CLOSE_YEAR, 0 as SALE_AMT, SUM(CASE WHEN A.DEPOSIT_TYPE IN ('1') THEN A.DEPOSIT_AMT ELSE 0 END) AS CASH_AMT, SUM(CASE WHEN A.DEPOSIT_TYPE IN ('2', '3') THEN A.DEPOSIT_AMT ELSE 0 END) AS PAPER_AMT, SUM(A.DEPOSIT_AMT + A.DEPOSIT_AMT * 0.1) AS DEPOSIT_TOTAL_AMT, 0 AS NOT_DEPOSIT_AMT FROM jmes.TBL_MONEY_DEPOSIT A WHERE DATE_FORMAT(STR_TO_DATE(A.DEPOSIT_DT, '%Y%m%d'), '%Y') = #{MONEY_MANAGE_STATUS_YEAR}
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY A.COMP_CD, A.ORDER_COMP_CD, DATE_FORMAT(STR_TO_DATE(A.DEPOSIT_DT, '%Y%m%d'), '%Y') UNION ALL
SELECT A.COMP_CD, A.ORDER_COMP_CD, #{MONEY_MANAGE_STATUS_YEAR} as CLOSE_YEAR, 0 as SALE_AMT, 0 AS CASH_AMT, 0 AS PAPER_AMT, 0 AS DEPOSIT_TOTAL_AMT, SUM(A.DEPOSIT_AMT + A.DEPOSIT_AMT * 0.1) AS NOT_DEPOSIT_AMT FROM jmes.TBL_MONEY_DEPOSIT A WHERE DATE_FORMAT(STR_TO_DATE(A.DEPOSIT_DT, '%Y%m%d'), '%Y') < #{MONEY_MANAGE_STATUS_YEAR}
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND A.DEPOSIT_TYPE IN ('2', '3') AND A.DUE_PAY_YN = 'N' GROUP BY A.COMP_CD, A.ORDER_COMP_CD ) Z LEFT OUTER JOIN jmes.TBL_MONEY_YEAR_NOTE Y ON Z.COMP_CD = Y.COMP_CD AND Z.ORDER_COMP_CD = Y.ORDER_COMP_CD AND Z.CLOSE_YEAR = Y.YYYY WHERE 1 = 1 GROUP BY Z.COMP_CD, Z.ORDER_COMP_CD, Z.CLOSE_YEAR ORDER BY Z.COMP_CD, Z.ORDER_COMP_CD

SELECT ROW_NUMBER() OVER () AS ROW_NUM, Z.COMP_CD, Z.COMP_NM, Z.ORDER_COMP_CD, Z.ORDER_COMP_NM, Z.CLOSE_MONTH, SUM(Z.ORDER_AMT) AS ORDER_AMT, SUM(Z.ORDER_AMT + Z.ORDER_AMT * 0.1) AS VAT_INCLUDED_AMOUNT, Z.CLOSE_MONTH_NM, E.NOTE FROM ( SELECT A.COMP_CD, (SELECT Z.COMP_NM FORM FROM jmes.TBL_COMPANY Z WHERE Z.COMP_CD = A.COMP_CD) AS COMP_NM, A.ORDER_COMP_CD, (SELECT Z.COMP_NM FORM FROM jmes.TBL_COMPANY Z WHERE Z.COMP_CD = A.ORDER_COMP_CD) AS ORDER_COMP_NM, D.CLOSE_MONTH, DATE_FORMAT(STR_TO_DATE(D.CLOSE_MONTH, '%Y%m'), '%Y-%m') as CLOSE_MONTH_NM, E.FINAL_NEGO_AMT AS ORDER_AMT FROM jmes.TBL_CONTROL A INNER JOIN jmes.TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ AND IFNULL(B.PART_NUM, '0') = '0' INNER JOIN jmes.TBL_CONTROL_PART_ORDER C ON B.CONTROL_SEQ = C.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ INNER JOIN jmes.TBL_MONTH_CLOSE_ORDER D ON A.CONTROL_SEQ = D.CONTROL_SEQ AND A.COMP_CD = D.COMP_CD AND A.ORDER_COMP_CD = D.ORDER_COMP_CD INNER JOIN jmes.TBL_MONTH_CLOSE E ON D.COMP_CD = E.COMP_CD AND D.CLOSE_MONTH = E.CLOSE_MONTH AND D.ORDER_COMP_CD = E.ORDER_COMP_CD AND D.CLOSE_VER = E.CLOSE_VER
AND E.CLOSE_MONTH BETWEEN CONCAT(#{MONEY_RECEIVE_CLOSE_YEAR_ST}, #{MONEY_RECEIVE_CLOSE_MONTH_ST}) AND CONCAT(#{MONEY_RECEIVE_CLOSE_YEAR_ED}, #{MONEY_RECEIVE_CLOSE_MONTH_ED})
WHERE A.DEL_YN = 'N'
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY A.COMP_CD, A.ORDER_COMP_CD, D.CLOSE_MONTH, D.CLOSE_VER ) Z LEFT OUTER JOIN jmes.TBL_MONEY_MONTH_NOTE E ON Z.CLOSE_MONTH = E.YYYYMM AND E.COMP_CD = Z.COMP_CD AND E.ORDER_COMP_CD = Z.ORDER_COMP_CD GROUP BY Z.COMP_CD, Z.ORDER_COMP_CD, Z.CLOSE_MONTH ORDER BY Z.COMP_CD, Z.ORDER_COMP_CD, Z.CLOSE_MONTH

SELECT A.DEPOSIT_SEQ, A.COMP_CD, (SELECT Z.COMP_NM FORM FROM jmes.TBL_COMPANY Z WHERE Z.COMP_CD = A.COMP_CD) AS COMP_CD_NM, A.ORDER_COMP_CD, (SELECT Z.COMP_NM FORM FROM jmes.TBL_COMPANY Z WHERE Z.COMP_CD = A.ORDER_COMP_CD) AS ORDER_COMP_NM, A.DEPOSIT_DT, DATE_FORMAT(STR_TO_DATE(A.DEPOSIT_DT, '%Y%m%d'), '%Y-%m-%d') AS DEPOSIT_DATE, A.DEPOSIT_AMT, A.DEPOSIT_TYPE, jmes.SF_GET_CODE_NM('1074', A.DEPOSIT_TYPE, #{LOGIN_LOCALE}) AS DEPOSIT_TYPE_NM, A.DUE_DT, DATE_FORMAT(STR_TO_DATE(A.DUE_DT, '%Y%m%d'), '%Y-%m-%d') AS DUE_DATE, CASE WHEN IFNULL(A.DUE_DT, '99991231') <= DATE_FORMAT(NOW(), '%Y%m%d') AND DEPOSIT_TYPE IN ('2', '3') THEN 'Y' ELSE 'N' END OVER_DATE_YN, A.DUE_PAY_YN, A.DUE_PAY_AMT, A.NOTE FROM jmes.TBL_MONEY_DEPOSIT A WHERE 1 = 1
AND A.COMP_CD = #{COMP_CD}
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND A.DEPOSIT_DT BETWEEN CONCAT(#{MONEY_RECEIVE_CLOSE_YEAR_ST}, #{MONEY_RECEIVE_CLOSE_MONTH_ST}, '01') AND CONCAT(#{MONEY_RECEIVE_CLOSE_YEAR_ED}, #{MONEY_RECEIVE_CLOSE_MONTH_ED}, '31')
ORDER BY A.COMP_CD, A.ORDER_COMP_CD, A.DEPOSIT_DT

SELECT ROW_NUMBER() OVER() AS ROW_NUM, A.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ, B.IMG_GFILE_SEQ, SF_GET_CODE_NM('1038', A.CONTROL_STATUS, #{LOGIN_LOCALE}) AS CONTROL_STATUS_NM, CASE WHEN IFNULL(A.EMERGENCY_YN, 'N') = 'Y' THEN '긴' ELSE SPACE(0) END EMERGENCY_YN_NM, A.ORDER_COMP_CD, SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM, SF_GET_DATE_F(C.ORDER_DUE_DT, '', '') AS ORDER_DUE_DT, SF_GET_DATE_F(C.DELIVERY_DT, '', '') AS DELIVERY_DT, SF_GET_DATE_F(C.OUT_FINISH_DT, '', '') AS OUT_FINISH_DT, A.CONTROL_NUM, C.ORDER_QTY, C.ORDER_NUM, C.DRAWING_NUM, SF_GET_PART_STATUS_NM(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ, #{LOGIN_LOCALE}) AS PART_STATUS, SF_GET_CODE_NM('1009', D.POP_POSITION, #{LOGIN_LOCALE}) AS LAST_POP_POSITION, H.INSPECT_GRADE, jmes.SF_GET_CODE_NM('1040', H.INSPECT_GRADE, #{LOGIN_LOCALE}) AS INSPECT_GRADE_NM, G.USER_NM, F.STAFF_NM FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ AND B.PART_NUM IS NULL AND B.WORK_TYPE NOT IN ('WTP050') INNER JOIN TBL_CONTROL_PART_ORDER C ON A.CONTROL_SEQ = C.CONTROL_SEQ
AND C.ORDER_DUE_DT = REPLACE(#{INNER_DUE_DT}, '/', SPACE(0))
AND C.ORDER_STAFF_SEQ = #{ORDER_STAFF_SEQ}
LEFT OUTER JOIN TBL_POP D ON B.CONTROL_SEQ = D.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = D.CONTROL_DETAIL_SEQ AND D.POP_SEQ = (SELECT MAX(X.POP_SEQ) FROM TBL_POP X WHERE X.CONTROL_SEQ = D.CONTROL_SEQ AND X.CONTROL_DETAIL_SEQ = D.CONTROL_DETAIL_SEQ) LEFT OUTER JOIN TBL_COMPANY E ON A.ORDER_COMP_CD = E.COMP_CD AND E.DEL_YN = 'N' LEFT OUTER JOIN TBL_COMPANY_STAFF F ON E.COMP_CD = F.COMP_CD AND C.ORDER_STAFF_SEQ = F.STAFF_SEQ LEFT OUTER JOIN TBL_USER G ON E.CHARGE_USER_ID = G.USER_ID AND G.DEL_YN = 'N' LEFT OUTER JOIN TBL_INSPECT H ON C.CONTROL_SEQ = H.CONTROL_SEQ AND C.CONTROL_DETAIL_SEQ = H.CONTROL_DETAIL_SEQ AND H.INSPECT_TYPE = '1' AND H.INSPECT_SEQ = (SELECT MAX(X.INSPECT_SEQ) FROM TBL_INSPECT X WHERE H.CONTROL_SEQ = X.CONTROL_SEQ AND H.CONTROL_DETAIL_SEQ = X.CONTROL_DETAIL_SEQ AND H.INSPECT_TYPE = '1') WHERE 1 = 1 AND A.DEL_YN = 'N' AND A.CONTROL_STATUS IN ('ORD001', 'ORD002', 'ORD003', 'ORD005')
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
ORDER BY A.CONTROL_NUM, C.ORDER_NUM, C.DRAWING_NUM

SELECT DATE_FORMAT(STR_TO_DATE(X.ORDER_DUE_DT, '%Y%m%d'), '%Y-%m-%d') AS `start` , CONCAT(Y.COMP_NM, ' ', X.OUT_FINISH_CNT, ' / ', X.ORDER_CNT) AS `title` , CASE WHEN X.OUT_FINISH_CNT = X.ORDER_CNT THEN '#E1F7FF' WHEN X.OUT_FINISH_CNT < X.ORDER_CNT AND DATE_FORMAT(NOW(),'%Y%m%d') <= X.ORDER_DUE_DT THEN '#FFFDA9' WHEN X.OUT_FINISH_CNT < X.ORDER_CNT AND DATE_FORMAT(NOW(),'%Y%m%d') > X.ORDER_DUE_DT THEN '#FFECDD' END AS `color` , CASE WHEN X.OUT_FINISH_CNT = X.ORDER_CNT THEN 3 WHEN X.OUT_FINISH_CNT < X.ORDER_CNT AND DATE_FORMAT(NOW(),'%Y%m%d') <= X.ORDER_DUE_DT THEN 2 WHEN X.OUT_FINISH_CNT < X.ORDER_CNT AND DATE_FORMAT(NOW(),'%Y%m%d') > X.ORDER_DUE_DT THEN 1 END AS SORT_1 , X.ORDER_CNT AS SORT_2 FROM ( SELECT C.ORDER_DUE_DT , A.ORDER_COMP_CD , SUM(CASE WHEN C.OUT_FINISH_DT IS NOT NULL THEN 1 ELSE 0 END) AS OUT_FINISH_CNT , COUNT(*) AS ORDER_CNT FROM jmes.TBL_CONTROL A INNER JOIN jmes.TBL_CONTROL_PART B ON B.CONTROL_SEQ = A.CONTROL_SEQ AND B.PART_NUM IS NULL AND B.WORK_TYPE NOT IN ('WTP050') INNER JOIN jmes.TBL_CONTROL_PART_ORDER C ON C.CONTROL_SEQ = A.CONTROL_SEQ AND C.ORDER_DUE_DT BETWEEN #{SEL_START_DATE} AND #{SEL_END_DATE} WHERE 1 = 1 AND A.DEL_YN = 'N' AND A.CONTROL_STATUS IN ('ORD001', 'ORD002', 'ORD003', 'ORD005')
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
GROUP BY C.ORDER_DUE_DT, A.ORDER_COMP_CD ) X LEFT OUTER JOIN TBL_COMPANY Y ON Y.COMP_CD = X.ORDER_COMP_CD ORDER BY X.ORDER_DUE_DT , CASE WHEN X.OUT_FINISH_CNT = X.ORDER_CNT THEN 3 WHEN X.OUT_FINISH_CNT < X.ORDER_CNT AND DATE_FORMAT(NOW(),'%Y%m%d') <= X.ORDER_DUE_DT THEN 2 WHEN X.OUT_FINISH_CNT < X.ORDER_CNT AND DATE_FORMAT(NOW(),'%Y%m%d') > X.ORDER_DUE_DT THEN 1 END , X.ORDER_CNT DESC

