    COMP_CD = #{COMP_CD}
    AND CLOSE_MONTH = #{CLOSE_MONTH}
    AND ORDER_COMP_CD = #{ORDER_COMP_CD}
    AND CLOSE_VER = #{CLOSE_VER}
    AND CONTROL_SEQ = #{CONTROL_SEQ}

