                        AND A.CONTROL_VER = B.CONTROL_VER AND IFNULL(A.DRAWING_VER, SPACE(0)) = IFNULL(C.DRAWING_VER, SPACE(0)))

mapper.select:
SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ, (SELECT MAX(G.BARCODE_NUM) FROM TBL_CONTROL_BARCODE G WHERE B.CONTROL_SEQ = G.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = G.CONTROL_DETAIL_SEQ AND G.DEL_YN = 'N') AS BARCODE_NUM, IFNULL(A.CONTROL_VER, SPACE(0)) AS CONTROL_VER, IFNULL(B.DRAWING_VER, SPACE(0)) AS DRAWING_VER, IFNULL(SF_GET_COMP_NM(A.ORDER_COMP_CD), SPACE(0)) AS ORDER_COMP_NM, IFNULL(A.CONTROL_NUM, SPACE(0)) AS CONTROL_NUM, CASE WHEN B.PART_NUM IS NULL THEN IFNULL(A.CONTROL_NUM, SPACE(0)) ELSE CONCAT(IFNULL(A.CONTROL_NUM, SPACE(0)), '#', B.PART_NUM) END AS CONTROL_NUM_PART, IFNULL(B.SIZE_TXT, SPACE(0)) AS SIZE_TXT, IFNULL(B.PART_NUM, SPACE(0)) AS PART_NUM, CASE WHEN A.TOTAL_SHEET IS NULL THEN SPACE(0) ELSE A.TOTAL_SHEET END AS TOTAL_SHEET, CASE WHEN B.SURFACE_TREAT IS NOT NULL AND B.MATERIAL_NOTE IS NOT NULL THEN CONCAT(SF_GET_CODE_NM('1039', B.SURFACE_TREAT, #{LOGIN_LOCALE}), ', ', IFNULL(B.MATERIAL_NOTE, SPACE(0))) WHEN B.SURFACE_TREAT IS NOT NULL AND B.MATERIAL_NOTE IS NULL THEN SF_GET_CODE_NM('1039', B.SURFACE_TREAT, #{LOGIN_LOCALE}) WHEN B.SURFACE_TREAT IS NULL AND B.MATERIAL_NOTE IS NOT NULL THEN B.MATERIAL_NOTE END AS SURFACE_TREAT_NM, IFNULL(SF_GET_CODE_NM('1035', B.MATERIAL_TYPE, #{LOGIN_LOCALE}), SPACE(0)) AS MATERIAL_TYPE_NM, IFNULL(SF_GET_CODE_NM('1027', B.MATERIAL_DETAIL, #{LOGIN_LOCALE}), SPACE(0)) AS MATERIAL_DETAIL_NM, IFNULL(SF_GET_CODE_NM('1033', B.WORK_TYPE, #{LOGIN_LOCALE}), SPACE(0)) AS WORK_TYPE_NM, CASE WHEN IFNULL(A.EMERGENCY_YN, 'N') = 'Y' THEN '긴급' ELSE SPACE(0) END AS EMERGENCY_BARCODE_NM, IFNULL(SF_GET_CODE_NM('1058', B.MATERIAL_FINISH_HEAT, #{LOGIN_LOCALE}), SPACE(0)) AS MATERIAL_FINISH_HEAT, IFNULL(SF_GET_CODE_NM('1059', A.MAIN_INSPECTION, #{LOGIN_LOCALE}), SPACE(0)) AS MAIN_INSPECTION_NM, IFNULL(B.PART_UNIT_QTY, 0) AS PART_UNIT_QTY, (SELECT IFNULL(SUM(M.ORDER_QTY), 0) FROM jmes.TBL_CONTROL_PART_ORDER M WHERE M.CONTROL_SEQ = A.CONTROL_SEQ) AS ORDER_QTY, CONCAT(IFNULL(SF_GET_CONTROL_PART_QTY(A.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ), 0), SPACE(0)) AS CONTROL_ORDER_QTY, IFNULL(jmes.SF_GET_SIDE_QTY(A.CONTROL_SEQ, 'ORIGINAL_SIDE_QTY') * IFNULL(B.PART_UNIT_QTY, 1), SPACE(0))AS ORIGINAL_SIDE_QTY, IFNULL(jmes.SF_GET_SIDE_QTY(A.CONTROL_SEQ, 'OTHER_SIDE_QTY') * IFNULL(B.PART_UNIT_QTY, 1), SPACE(0)) AS OTHER_SIDE_QTY, IFNULL(SF_GET_DATE_F(B.INNER_DUE_DT, 'MM', ''), SPACE(0)) AS INNER_DUE_DT, (SELECT M.FILE_PATH FROM TBL_FILE M WHERE M.GFILE_SEQ = B.IMG_GFILE_SEQ) AS IMAGE_PATH, IFNULL((SELECT CASE WHEN COUNT(DISTINCT M.DRAWING_NUM) > 1 THEN 'Y' ELSE 'N' END FROM TBL_CONTROL_PART_ORDER M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ GROUP BY M.CONTROL_SEQ, M.CONTROL_DETAIL_SEQ), 'N') AS MULTI_ORDER_YN FROM TBL_CONTROL A, TBL_CONTROL_PART B WHERE 1 = 1 AND A.CONTROL_SEQ = B.CONTROL_SEQ AND A.DEL_YN = 'N' AND CONCAT(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ) IN
#{bean}
ORDER BY B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ

