SET
    UNIT_FINAL_EST_AMT = REPLACE(#{UNIT_FINAL_EST_AMT, jdbcType=VARCHAR}, ',', SPACE(0)),
    UNIT_FINAL_AMT = REPLACE(#{UNIT_FINAL_AMT, jdbcType=VARCHAR}, ',', SPACE(0)),
    UPDATE_DT = NOW(),
    UPDATE_ID = #{LOGIN_USER_ID}
WHERE 1 = 1
    AND CONTROL_SEQ = #{CONTROL_SEQ}
    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}

UPDATE TBL_CONTROL_PART SET PART_NUM = #{PART_NUM}, DRAWING_NUM = #{ORDER_DRAWING_NUM}, PREV_DRAWING_NUM = #{PREV_DRAWING_NUM}, WORK_TYPE = #{WORK_TYPE}, OUTSIDE_YN = IFNULL(#{OUTSIDE_YN}, 'N'), WORK_FACTORY = CASE WHEN #{WORK_FACTORY} = 'null' THEN NULL ELSE #{WORK_FACTORY} END, MATERIAL_SUPPLY_YN = IFNULL(#{MATERIAL_SUPPLY_YN}, 'N'), INNER_DUE_DT = DATE_FORMAT(#{INNER_DUE_DT}, '%Y%m%d'), SIZE_TXT = #{SIZE_TXT}, SIZE_TYPE = SF_GET_SIZE_TXT('SIZE_TYPE', #{SIZE_TXT}), SIZE_W = SF_GET_SIZE_TXT('SIZE_W', #{SIZE_TXT}), SIZE_H = SF_GET_SIZE_TXT('SIZE_H', #{SIZE_TXT}), SIZE_T = SF_GET_SIZE_TXT('SIZE_T', #{SIZE_TXT}), SIZE_D = SF_GET_SIZE_TXT('SIZE_D', #{SIZE_TXT}), SIZE_L = SF_GET_SIZE_TXT('SIZE_L', #{SIZE_TXT}), MATERIAL_DETAIL = #{MATERIAL_DETAIL}, MATERIAL_TYPE = SF_GET_ETC_CD('1027', #{MATERIAL_DETAIL}), MATERIAL_KIND = #{MATERIAL_KIND}, SURFACE_TREAT = #{SURFACE_TREAT}, MATERIAL_NOTE = #{MATERIAL_NOTE}, PART_UNIT_QTY = #{PART_UNIT_QTY}, MATERIAL_FINISH_GRIND = #{MATERIAL_FINISH_GRIND}, MATERIAL_FINISH_HEAT = #{MATERIAL_FINISH_HEAT}, UNIT_MATERIAL_AMT = #{UNIT_MATERIAL_AMT}, UNIT_MATERIAL_FINISH_GRIND_AMT = #{UNIT_MATERIAL_FINISH_GRIND_AMT}, UNIT_MATERIAL_FINISH_HEAT_AMT = #{UNIT_MATERIAL_FINISH_HEAT_AMT}, UNIT_SURFACE_AMT = #{UNIT_SURFACE_AMT}, UNIT_PROCESS_AMT = #{UNIT_PROCESS_AMT}, UNIT_ETC_AMT = #{UNIT_ETC_AMT}, UNIT_AMT_NOTE = #{UNIT_AMT_NOTE}, ETC_GFILE_SEQ = #{ETC_GFILE_SEQ}, OUTSIDE_HOPE_DUE_DT = CASE WHEN OUTSIDE_YN = 'Y' AND OUTSIDE_HOPE_DUE_DT IS NULL THEN DATE_FORMAT(DATE_SUB(INNER_DUE_DT, INTERVAL 1 DAY), '%Y%m%d') ELSE OUTSIDE_HOPE_DUE_DT END, UPDATE_DT = NOW(), UPDATE_ID = #{LOGIN_USER_ID} WHERE 1 = 1 AND CONTROL_SEQ = #{CONTROL_SEQ}
AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}
AND CONTROL_DETAIL_SEQ = ( SELECT A.CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART A WHERE A.CONTROL_SEQ = #{CONTROL_SEQ} AND A.PART_NUM = #{PART_NUM})

