mapper.select:
SELECT ROW_NUMBER () OVER () AS ROWNUM, CPRO.ORDER_STATUS, (SELECT CODE_NM FROM TBL_CODE_LANG WHERE HIGH_CD = '1024' AND CODE_CD = CPRO.ORDER_STATUS AND LANG_CD = 'KR') AS ORDER_STATUS_NM, DATE_FORMAT(CPRO.STATUS_DT, '%m-%d %H:%i') AS STATUS_DT, C.COMP_CD, (SELECT CODE_NM FROM TBL_CODE_LANG WHERE CODE_CD = C.COMP_CD AND LANG_CD = 'KR' LIMIT 1) AS COMP_NM, -- LIMIT 1 임시 C.ORDER_COMP_CD, -- 발주업체 (SELECT COMP_NM FROM TBL_COMPANY WHERE ORDER_COMP_CD = C.ORDER_COMP_CD LIMIT 1) AS ORDER_COMP_NM, -- 발주업체이름(JOIN) C.ORDER_STAFF_SEQ, (SELECT STAFF_NM FROM TBL_COMPANY_STAFF WHERE STAFF_SEQ = C.ORDER_STAFF_SEQ LIMIT 1) AS ORDER_STAFF_NM, C.DESIGNER_NM, -- 설계자 C.NOTE, -- 비고 -- '',-- 거래명세서 no ㅇㄷ? C.CONTROL_NUM, CP.PART_NUM, CP.DRAWING_NUM, CP.ITEM_NM, CP.WORK_TYPE, (SELECT CODE_NM FROM TBL_CODE_LANG WHERE HIGH_CD = '1013' AND CODE_CD = CP.WORK_TYPE AND LANG_CD = 'KR') AS WORK_NM, CP.OUTSIDE_YN, CP.PART_UNIT_QTY, CPO.ORDER_NUM, CPO.ORDER_QTY, CP.MATERIAL_UNIT_PRICE, CP.SURFACE_UNIT_PRICE FROM TBL_CONTROL C INNER JOIN TBL_CONTROL_PROGRESS CPRO ON C.CONTROL_SEQ = CPRO.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART CP ON C.CONTROL_SEQ = CP.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER CPO ON CP.CONTROL_SEQ = CPO.CONTROL_SEQ AND CP.CONTROL_DETAIL_SEQ = CPO.CONTROL_DETAIL_SEQ WHERE 1 = 1
AND C.CONTROL_NUM = #{CONTROL_NUMBER}
AND C.COMP_CD = #{CORPORATION}
AND CP.DRAWING_NUM = #{DRAWING_NUMBER}
AND CP.ITEM_NM = #{PRODUCT_NAME}
AND CP.WORK_TYPE = #{WORK_TYPE}
AND CPO.ORDER_NUM = #{ORDER_NUMBER}
ORDER BY C.CONTROL_SEQ, CP.PART_NUM
