mapper.select:

WHERE 1 = 1

     AND A.CONTROL_NUM = #{CONTROL_NUM}


     AND A.COMP_CD = #{COMP_CD}


     AND B.DRAWING_NUM = #{DRAWING_NUM}


     AND B.ITEM_NM = #{ITEM_NM}


     AND B.WORK_TYPE = #{WORK_TYPE}


     AND C.ORDER_NUM = #{ORDER_NUM}


     AND A.ORDER_STATUS = #{ORDER_STATUS}


    AND B.OUTSIDE_YN NOT IN ('Y')

ORDER BY
    A.CONTROL_SEQ,
    B.PART_NUM

SELECT ORDER_COMP_CD, ORDER_COMP_NM, CLOSE_MONTH, CLOSE_MONTH_TRAN, CLOSE_VER, SUM(ORDER_QTY) AS ORDER_QTY, SUM(UNIT_FINAL_AMT) AS UNIT_FINAL_AMT FROM ( SELECT A.ORDER_COMP_CD, SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM, D.CLOSE_MONTH, DATE_FORMAT(STR_TO_DATE(D.CLOSE_MONTH, '%Y%m'), '%Y.%m월' ) AS CLOSE_MONTH_TRAN, D.CLOSE_VER, SUM(C.ORDER_QTY) AS ORDER_QTY, SUM(B.UNIT_FINAL_AMT) AS UNIT_FINAL_AMT FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER C ON B.CONTROL_SEQ = B.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ INNER JOIN TBL_CONTROL_CLOSE D ON A.CONTROL_SEQ = D.CONTROL_SEQ AND D.CLOSE_MONTH = CONCAT(#{MONTH_FINISH_YEAR}, #{MONTH_FINISH_MONTH}) AND D.CLOSE_VER = #{CLOSE_VER} WHERE A.ORDER_COMP_CD IN (${ORDER_COMP_CD}) GROUP BY A.ORDER_COMP_CD UNION ALL SELECT A.ORDER_COMP_CD, SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM, CONCAT(#{MONTH_FINISH_YEAR}, #{MONTH_FINISH_MONTH}) AS TRAN, DATE_FORMAT(STR_TO_DATE(CONCAT(#{MONTH_FINISH_YEAR}, #{MONTH_FINISH_MONTH}), '%Y%m'), '%Y.%m월' ) AS CLOSE_MONTH_TRAN, '-' AS CLOSE_VER, '-' AS ORDER_QTY, '-' AS UNIT_FINAL_AMT FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ WHERE 1 = 1 AND A.ORDER_COMP_CD IN (${ORDER_COMP_CD}) AND NOT EXISTS (SELECT * FROM TBL_CONTROL_CLOSE WHERE B.CONTROL_SEQ = CONTROL_SEQ AND CLOSE_MONTH = CONCAT(#{MONTH_FINISH_YEAR}, #{MONTH_FINISH_MONTH})) GROUP BY A.ORDER_COMP_CD
UNION ALL SELECT A.ORDER_COMP_CD, SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM, CONCAT(#{MONTH_FINISH_YEAR}, #{MONTH_FINISH_MONTH}) AS CLOSE_MONTH, DATE_FORMAT(STR_TO_DATE(CONCAT(#{MONTH_FINISH_YEAR}, #{MONTH_FINISH_MONTH}), '%Y%m'), '%Y.%m월' ) AS CLOSE_MONTH_TRAN, #{CLOSE_VER}, C.ORDER_QTY, B.UNIT_FINAL_AMT FROM TBL_CONTROL A INNER JOIN TBL_CONTROL_PART B ON A.CONTROL_SEQ = B.CONTROL_SEQ INNER JOIN TBL_CONTROL_PART_ORDER C ON B.CONTROL_SEQ = B.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ WHERE A.CONTROL_SEQ = #{item.CONTROL_SEQ} GROUP BY A.ORDER_COMP_CD
) X GROUP BY ORDER_COMP_CD

mapper.insert:
INSERT INTO TBL_CONTROL_PART_ORDER ( CONTROL_SEQ, CONTROL_DETAIL_SEQ, ORDER_SEQ, ORDER_NUM, ORDER_QTY, ORDER_DUE_DT, DELIVERY_DT
) VALUES
( (SELECT CONTROL_SEQ FROM TBL_CONTROL A WHERE A.CONTROL_NUM = #{item.CONTROL_NUM}), (SELECT B.CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART B INNER JOIN TBL_CONTROL C ON B.CONTROL_SEQ = C.CONTROL_SEQ WHERE C.CONTROL_NUM = #{item.CONTROL_NUM} AND B.CONTROL_DETAIL_SEQ NOT IN (SELECT CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART_ORDER D WHERE D.CONTROL_SEQ = B.CONTROL_SEQ) ORDER BY B.CONTROL_DETAIL_SEQ LIMIT 1), NEXTVAL(SEQ_CONTROL_PART_ORDER), #{item.ORDER_NUM}, (SELECT IF(#{item.ORDER_QTY} = '', NULL, CAST(#{item.ORDER_QTY} AS UNSIGNED INTEGER))), #{item.ORDER_DUE_DT}, #{item.DELIVERY_DT}
)

INSERT INTO TBL_CONTROL_PROGRESS ( CONTROL_SEQ, SEQ, ORDER_STATUS, STATUS_DT
) VALUES
( (SELECT A.CONTROL_SEQ FROM TBL_CONTROL A WHERE A.CONTROL_NUM = #{item.CONTROL_NUM}), NEXTVAL(SEQ_CONTROL_PROGRESS), #{item.STATUS}, NOW()
)

INSERT INTO TBL_CONTROL_PART_PROGRESS ( CONTROL_SEQ, CONTROL_DETAIL_SEQ, SEQ, PART_STATUS, STATUS_DT
) VALUES
( (SELECT A.CONTROL_SEQ FROM TBL_CONTROL A WHERE A.CONTROL_NUM = #{item.CONTROL_NUM}), (SELECT B.CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART B INNER JOIN TBL_CONTROL C ON B.CONTROL_SEQ = C.CONTROL_SEQ WHERE C.CONTROL_NUM = #{item.CONTROL_NUM} AND B.CONTROL_DETAIL_SEQ NOT IN (SELECT D.CONTROL_DETAIL_SEQ FROM TBL_CONTROL_PART_PROGRESS D WHERE D.CONTROL_SEQ = B.CONTROL_SEQ) ORDER BY B.CONTROL_DETAIL_SEQ LIMIT 1), NEXTVAL(SEQ_CONTROL_PART_PROGRESS), #{item.STATUS}, NOW() )

mapper.select:
WHERE 1 = 1 AND B.PART_STATUS = 'PRO01' AND B.WORK_TYPE NOT IN ('FCT02') AND B.OUTSIDE_YN = 'Y'
AND C.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.MATERIAL_TYPE = #{MATERIAL_TYPE}
ORDER BY C.CONTROL_SEQ, B.PART_NUM
,C.ORDER_DUE_DT DESC
,B.INNER_DUE_DT DESC


WHERE 1 = 1
    AND A.CONTROL_STATUS = 'PRO16'

    AND A.COMP_CD = #{COMP_CD}


    AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}


    AND B.DRAWING_NUM = #{DRAWING_NUM}


    AND B.ITEM_NM = #{ITEM_NM}


    AND B.MATERIAL_KIND = #{MATERIAL_KIND}


    AND A.CONTROL_SEQ = #{CONTROL_SEQ}


    AND A.C.MODULE_NM = #{MODULE_NM}

ORDER BY

    A.EMERGENCY_YN DESC,

A.CONTROL_SEQ,
B.PART_NUM


        ,C.ORDER_DUE_DT DESC


        ,B.INNER_DUE_DT DESC
