mapper.select:
SELECT ROW_NUMBER () OVER(ORDER BY Z.WEEK_SEQ IS NULL ASC, Z.WEEK_SEQ, Z.DT IS NULL ASC, Z.DT) AS ROW_NUM , #{WORK_FACTORY} AS WORK_FACTORY , Z.WEEK_SEQ , Z.DT , CASE WHEN Z.WEEK_SEQ IS NULL THEN '총계' WHEN Z.DT IS NULL THEN '합계' ELSE CONCAT(DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%e'),'일') END AS CAL_DT_NM , CASE WHEN Z.DT IS NULL THEN '' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = '0' THEN '일' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = '1' THEN '월' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = '2' THEN '화' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = '3' THEN '수' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = '4' THEN '목' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = '5' THEN '금' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = '6' THEN '토' END AS WEEK_DAY_NM , Z.PART_CNT , Z.PART_QTY , Z.FORECAST_AMT , Z.DT_GOAL_AMT , CASE WHEN IFNULL(Z.DT_GOAL_AMT,0) = 0 THEN NULL ELSE FORMAT(IFNULL(Z.FORECAST_AMT,0) / IFNULL(Z.DT_GOAL_AMT,0) * 100,1) END AS GOAL_RATIO , Z.WORKING_TIME , Z.ERROR_CNT , Z.ERROR_QTY , (SELECT M.NOTE FROM jmes.TBL_WORKING_DAY_NOTE M WHERE M.WORKING_DT = Z.DT) AS NOTE FROM ( SELECT X.WEEK_SEQ , X.DT , SUM(IFNULL(Y.PART_CNT,0)) AS PART_CNT , SUM(IFNULL(Y.PART_QTY,0)) AS PART_QTY , SUM(IFNULL(Y.FORECAST_AMT,0)) AS FORECAST_AMT , SUM(IFNULL(X.DT_GOAL_AMT,0)) AS DT_GOAL_AMT , SUM(X.WORKING_TIME) AS WORKING_TIME , SUM(IFNULL(Y.ERROR_CNT,0)) AS ERROR_CNT , SUM(IFNULL(Y.ERROR_QTY,0)) AS ERROR_QTY FROM ( SELECT F.DT , DATE_FORMAT(STR_TO_DATE(F.DT,'%Y%m%d'),'%u') AS WEEK_SEQ , CASE WHEN F.WEEK_DAY > 0 THEN ( SELECT ROUND(M.GOAL_AMT / N.DT_CNT,0) AS DT_GOAL_AMT FROM ( SELECT SUM(M.PRODUCTION_GOAL_AMT) AS GOAL_AMT FROM jmes.TBL_SALES_GOAL M WHERE YYYYMM = CONCAT(#{YEAR}, #{MONTH})
AND WORK_FACTORY = #{WORK_FACTORY}
) M , ( SELECT COUNT(*) AS DT_CNT FROM jmes.TBL_YYYYMMDD A WHERE A.DT LIKE CONCAT(#{YEAR}, #{MONTH},'%') AND DATE_FORMAT(STR_TO_DATE(A.DT,'%Y%m%d'),'%w') > 0 ) N ) END AS DT_GOAL_AMT , ( SELECT M.WORKING_TIME FROM jmes.TBL_WORKING_WEEK_TIME M WHERE M.WORK_FACTORY = #{WORK_FACTORY} AND M.WEEK_DAY = F.WEEK_DAY ) AS WORKING_TIME FROM ( SELECT A.DT , DATE_FORMAT(STR_TO_DATE(A.DT,'%Y%m%d'),'%w') AS WEEK_DAY FROM jmes.TBL_YYYYMMDD A WHERE A.DT LIKE CONCAT(#{YEAR}, #{MONTH},'%') ) F ) X LEFT OUTER JOIN ( SELECT P.WORKING_FINISH_DT , SUM(P.PART_CNT) AS PART_CNT , SUM(P.PART_QTY) AS PART_QTY , SUM(P.FORECAST_UNIT_AMT * P.PART_QTY) AS FORECAST_AMT , SUM(P.ERROR_CNT) AS ERROR_CNT , SUM(P.ERROR_QTY) AS ERROR_QTY FROM ( SELECT A.CONTROL_SEQ, A.CONTROL_DETAIL_SEQ , DATE_FORMAT(DATE_ADD(A.INNER_WORK_FINISH_DT, INTERVAL -1 * (CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT)) MINUTE),'%Y%m%d') AS WORKING_FINISH_DT , 1 AS PART_CNT , jmes.SF_GET_CONTROL_PART_QTY(A.CONTROL_SEQ, A.CONTROL_DETAIL_SEQ) AS PART_QTY , IFNULL(A.FORECAST_UNIT_AMT,0) AS FORECAST_UNIT_AMT , ROW_NUMBER() OVER (PARTITION BY A.CONTROL_SEQ, A.CONTROL_DETAIL_SEQ ORDER BY B.INSPECT_SEQ) AS INSPECT_RNUM , CASE WHEN B.INSPECT_GRADE = 'GRD040' THEN 1 ELSE 0 END AS ERROR_CNT , CASE WHEN B.INSPECT_GRADE = 'GRD040' THEN B.ERROR_QTY ELSE 0 END AS ERROR_QTY FROM jmes.TBL_CONTROL_PART A LEFT OUTER JOIN jmes.TBL_INSPECT B ON A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND B.INSPECT_TYPE = '1' WHERE A.WORK_TYPE != 'WTP020'
AND WORK_FACTORY = #{WORK_FACTORY}
AND A.INNER_WORK_FINISH_DT >= DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND A.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL 1 MONTH), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND NOT EXISTS (SELECT 1 FROM jmes.TBL_CONTROL M WHERE M.CONTROL_SEQ = A.CONTROL_SEQ AND M.DEL_YN = 'Y') ) P WHERE P.INSPECT_RNUM = 1 GROUP BY P.WORKING_FINISH_DT ) Y ON X.DT = Y.WORKING_FINISH_DT GROUP BY X.WEEK_SEQ, X.DT WITH ROLLUP ) Z ORDER BY Z.WEEK_SEQ IS NULL ASC, Z.WEEK_SEQ, Z.DT IS NULL ASC, Z.DT

SELECT ROW_NUMBER() OVER(ORDER BY Z.CONTROL_SEQ, Z.CONTROL_DETAIL_SEQ ) AS ROW_NUM , Z.CONTROL_SEQ , Z.CONTROL_DETAIL_SEQ , Z.IMG_GFILE_SEQ , Z.ORDER_COMP_NM , Z.WORK_FACTORY_NM , CASE WHEN Z.PART_NUM IS NULL THEN Z.CONTROL_NUM ELSE CONCAT(Z.CONTROL_NUM, ' #', Z.PART_NUM) END AS CONTROL_NUM , Z.INNER_DUE_DT , Z.PART_QTY , Z.INNER_WORK_FINISH_DT , FORMAT(Z.WORK_TIME / 60,1) AS WORK_TIME , Z.FORECAST_UNIT_AMT , Z.PART_QTY * Z.FORECAST_UNIT_AMT AS TOTAL_AMT , CASE WHEN IFNULL(Z.WORK_TIME,0) = 0 THEN NULL ELSE FORMAT(Z.PART_QTY * Z.UNIT_FINAL_AMT / (Z.WORK_TIME / 60 / 60),0) END AS PRICE_PER_HOUR , CASE WHEN Z.PREV_DRAWING_NUM IS NOT NULL THEN ( SELECT M.UNIT_FINAL_AMT FROM jmes.TBL_CONTROL_PART_ORDER_MAIN M, jmes.TBL_CONTROL N WHERE M.CONTROL_SEQ = N.CONTROL_SEQ AND N.CONTROL_STATUS = 'ORD003' AND N.DEL_YN = 'N' AND M.DRAWING_NUM = Z.PREV_DRAWING_NUM ORDER BY M.CONTROL_SEQ DESC LIMIT 1) ELSE ( SELECT M.UNIT_FINAL_AMT FROM jmes.TBL_CONTROL_PART_ORDER_MAIN M, jmes.TBL_CONTROL N WHERE M.CONTROL_SEQ = N.CONTROL_SEQ AND N.CONTROL_STATUS = 'ORD003' AND N.DEL_YN = 'N' AND N.CONTROL_SEQ != Z.CONTROL_SEQ AND M.DRAWING_NUM = Z.DRAWING_NUM ORDER BY M.CONTROL_SEQ DESC LIMIT 1) END AS PREV_UNIT_FINAL_AMT , Z.UNIT_FINAL_EST_AMT , Z.UNIT_FINAL_AMT , Z.INSPECT_GRADE_NM , Z.INSPECT_RESULT_NM , Z.ERROR_QTY , Z.NOTE FROM ( SELECT P.CONTROL_SEQ , P.CONTROL_DETAIL_SEQ , P.IMG_GFILE_SEQ , jmes.SF_GET_COMP_NM(X.ORDER_COMP_CD) AS ORDER_COMP_NM , jmes.SF_GET_CODE_NM('1014',Y.WORK_FACTORY,#{LOGIN_LOCALE}) AS WORK_FACTORY_NM , X.CONTROL_NUM , Y.PART_NUM , jmes.SF_GET_DATE_F(Y.INNER_DUE_DT,'','') AS INNER_DUE_DT , P.PART_QTY , jmes.SF_GET_DATE_F(Y.INNER_WORK_FINISH_DT,'','M') AS INNER_WORK_FINISH_DT , (SELECT ROUND(SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)),0) FROM jmes.TBL_MCT_WORK M WHERE M.CONTROL_SEQ = P.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = P.CONTROL_DETAIL_SEQ) AS WORK_TIME , Y.FORECAST_UNIT_AMT , M.UNIT_FINAL_EST_AMT , M.UNIT_FINAL_AMT , jmes.SF_GET_CODE_NM('1040',P.INSPECT_GRADE,#{LOGIN_LOCALE}) AS INSPECT_GRADE_NM , jmes.SF_GET_CODE_NM('1020',P.INSPECT_RESULT,#{LOGIN_LOCALE}) AS INSPECT_RESULT_NM , P.ERROR_QTY , X.NOTE , Y.DRAWING_NUM , Y.PREV_DRAWING_NUM FROM ( SELECT A.CONTROL_SEQ, A.CONTROL_DETAIL_SEQ, A.IMG_GFILE_SEQ , DATE_FORMAT(DATE_ADD(A.INNER_WORK_FINISH_DT, INTERVAL -1 * (CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT)) MINUTE),'%Y%m%d') AS WORKING_FINISH_DT , jmes.SF_GET_CONTROL_PART_QTY(A.CONTROL_SEQ, A.CONTROL_DETAIL_SEQ) AS PART_QTY , IFNULL(A.FORECAST_UNIT_AMT,0) AS FORECAST_UNIT_AMT , ROW_NUMBER() OVER (PARTITION BY A.CONTROL_SEQ, A.CONTROL_DETAIL_SEQ ORDER BY B.INSPECT_SEQ) AS INSPECT_RNUM , B.INSPECT_GRADE , B.INSPECT_RESULT , CASE WHEN B.INSPECT_GRADE = 'GRD040' THEN B.ERROR_QTY ELSE 0 END AS ERROR_QTY FROM jmes.TBL_CONTROL_PART A LEFT OUTER JOIN jmes.TBL_INSPECT B ON A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND B.INSPECT_TYPE = '1' WHERE A.INNER_WORK_FINISH_DT IS NOT NULL AND A.INNER_WORK_FINISH_DT >= DATE_ADD(STR_TO_DATE(#{DT},'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND A.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(#{DT},'%Y%m%d'), INTERVAL 1 DAY), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) ) P, jmes.TBL_CONTROL X, jmes.TBL_CONTROL_PART Y, jmes.TBL_CONTROL_PART_ORDER_MAIN M WHERE P.CONTROL_SEQ = X.CONTROL_SEQ AND P.CONTROL_SEQ = Y.CONTROL_SEQ AND P.CONTROL_DETAIL_SEQ = Y.CONTROL_DETAIL_SEQ AND P.CONTROL_SEQ = M.CONTROL_SEQ AND P.INSPECT_RNUM = 1 AND X.DEL_YN = 'N' AND Y.WORK_TYPE != 'WTP020'
AND Y.WORK_FACTORY = #{WORK_FACTORY}
) Z

mapper.insert:
        INSERT INTO TBL_WORKING_DAY_NOTE (
            WORKING_DT,
            NOTE,
            INSERT_ID
        ) VALUES (
            #{DT},
            #{NOTE},
            #{LOGIN_USER_ID}
        ) ON DUPLICATE KEY
	        UPDATE
	            NOTE = #{NOTE},
                UPDATE_DT = NOW(),
	            UPDATE_ID = #{LOGIN_USER_ID}

mapper.update:
UPDATE TBL_CONTROL
SET
    NOTE = #{NOTE},
    UPDATE_DT = NOW(),
    UPDATE_ID = #{LOGIN_USER_ID}
WHERE CONTROL_SEQ = #{CONTROL_SEQ}

UPDATE TBL_CONTROL_PART
SET
    FORECAST_UNIT_AMT = #{FORECAST_UNIT_AMT},
    UPDATE_DT = NOW(),
    UPDATE_ID = #{LOGIN_USER_ID}
WHERE 1 = 1
    AND CONTROL_SEQ = #{CONTROL_SEQ}
    AND CONTROL_DETAIL_SEQ = #{CONTROL_DETAIL_SEQ}

mapper.select:
SELECT ROW_NUMBER () OVER (ORDER BY Z.WEEK_SEQ IS NULL ASC, Z.WEEK_SEQ, Z.DT IS NULL ASC, Z.DT) AS ROW_NUM , #{COMP_CD} AS COMP_CD , #{ORDER_COMP_CD} AS ORDER_COMP_CD , Z.WEEK_SEQ , Z.DT , CASE WHEN Z.WEEK_SEQ IS NULL THEN '총계' WHEN Z.DT IS NULL THEN '합계' ELSE CONCAT(DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%e'),'일') END AS CAL_DT_NM , CASE WHEN Z.DT IS NULL THEN '' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = 0 THEN '일' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = 1 THEN '월' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = 2 THEN '화' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = 3 THEN '수' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = 4 THEN '목' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = 5 THEN '금' WHEN DATE_FORMAT(STR_TO_DATE(Z.DT,'%Y%m%d'),'%w') = 6 THEN '토' END AS WEEK_DAY_NM , Z.PART_CNT , Z.PART_QTY , Z.OUTSIDE_CNT , Z.OUTSIDE_QTY , Z.INNER_WORK_FINISH_CNT , Z.INNER_WORK_FINISH_QTY , Z.OUT_FINISH_CNT , Z.OUT_FINISH_QTY , Z.LATE_CNT , Z.LATE_QTY , CASE WHEN IFNULL(Z.PART_CNT,0) = 0 THEN NULL ELSE Z.LATE_CNT / Z.PART_CNT * 100 END AS LATE_RATIO , CASE WHEN IFNULL(Z.PART_CNT,0) = 0 THEN NULL ELSE Z.OUTSIDE_CNT / Z.PART_CNT * 100 END AS OUTSIDE_RATIO , Z.FORECAST_UNIT_AMT , Z.DT_GOAL_AMT , CASE WHEN IFNULL(Z.DT_GOAL_AMT,0) = 0 THEN NULL ELSE CONCAT(FORMAT(IFNULL(Z.FORECAST_UNIT_AMT,0) / IFNULL(Z.DT_GOAL_AMT,0) * 100,1),'%') END AS GOAL_RATIO , Z.ERROR_CNT , Z.ERROR_QTY , Z.RETURN_CNT , Z.RETURN_QTY , (SELECT M.NOTE FROM jmes.TBL_WORKING_DAY_NOTE M WHERE M.WORKING_DT = Z.DT) AS NOTE FROM ( SELECT X.WEEK_SEQ , X.DT , SUM(IFNULL(Y.PART_CNT,0)) AS PART_CNT , SUM(IFNULL(Y.PART_QTY,0)) AS PART_QTY , SUM(IFNULL(Y.OUTSIDE_CNT,0)) AS OUTSIDE_CNT , SUM(IFNULL(Y.OUTSIDE_QTY,0)) AS OUTSIDE_QTY , SUM(IFNULL(Y.INNER_WORK_FINISH_CNT,0)) AS INNER_WORK_FINISH_CNT , SUM(IFNULL(Y.INNER_WORK_FINISH_QTY,0)) AS INNER_WORK_FINISH_QTY , SUM(IFNULL(Y.OUT_FINISH_CNT,0)) AS OUT_FINISH_CNT , SUM(IFNULL(Y.OUT_FINISH_QTY,0)) AS OUT_FINISH_QTY , SUM(IFNULL(Y.LATE_CNT,0)) AS LATE_CNT , SUM(IFNULL(Y.LATE_QTY,0)) AS LATE_QTY , SUM(IFNULL(Y.FORECAST_UNIT_AMT,0)) AS FORECAST_UNIT_AMT , SUM(IFNULL(X.DT_GOAL_AMT,0)) AS DT_GOAL_AMT , SUM(IFNULL(Y.ERROR_CNT,0)) AS ERROR_CNT , SUM(IFNULL(Y.ERROR_QTY,0)) AS ERROR_QTY , SUM(IFNULL(Y.RETURN_CNT,0)) AS RETURN_CNT , SUM(IFNULL(Y.RETURN_QTY,0)) AS RETURN_QTY FROM ( SELECT F.DT , DATE_FORMAT(STR_TO_DATE(F.DT,'%Y%m%d'),'%u') AS WEEK_SEQ , CASE WHEN F.WEEK_DAY > 0 THEN ( SELECT ROUND(M.GOAL_AMT / N.DT_CNT,0) AS DT_GOAL_AMT FROM ( SELECT SUM(M.SALES_GOAL_AMT) AS GOAL_AMT FROM jmes.TBL_SALES_GOAL M WHERE YYYYMM = CONCAT(#{YEAR}, #{MONTH}) ) M , ( SELECT COUNT(*) AS DT_CNT FROM jmes.TBL_YYYYMMDD A WHERE A.DT LIKE CONCAT(CONCAT(#{YEAR}, #{MONTH}),'%') AND DATE_FORMAT(STR_TO_DATE(A.DT,'%Y%m%d'),'%w') > 0 ) N ) END AS DT_GOAL_AMT FROM ( SELECT A.DT , DATE_FORMAT(STR_TO_DATE(A.DT,'%Y%m%d'),'%w') AS WEEK_DAY FROM jmes.TBL_YYYYMMDD A WHERE A.DT LIKE CONCAT(CONCAT(#{YEAR}, #{MONTH}),'%') ) F ) X LEFT OUTER JOIN ( SELECT T.ORDER_DUE_DT , SUM(T.PART_CNT) AS PART_CNT , SUM(T.PART_QTY) AS PART_QTY , SUM(CASE WHEN T.OUTSIDE_YN = 'Y' THEN 1 ELSE 0 END) AS OUTSIDE_CNT , SUM(CASE WHEN T.OUTSIDE_YN = 'Y' THEN T.PART_QTY ELSE 0 END) AS OUTSIDE_QTY , SUM(CASE WHEN T.INNER_WORK_FINISH_YN = 'Y' THEN 1 ELSE 0 END) AS INNER_WORK_FINISH_CNT , SUM(CASE WHEN T.INNER_WORK_FINISH_YN = 'Y' THEN T.PART_QTY ELSE 0 END) AS INNER_WORK_FINISH_QTY , SUM(CASE WHEN T.OUT_FINISH_YN = 'Y' THEN 1 ELSE 0 END) AS OUT_FINISH_CNT , SUM(CASE WHEN T.OUT_FINISH_YN = 'Y' THEN T.PART_QTY ELSE 0 END) AS OUT_FINISH_QTY , SUM(CASE WHEN T.OUT_FINISH_DT > T.ORDER_DUE_DT THEN 1 ELSE 0 END) AS LATE_CNT , SUM(CASE WHEN T.OUT_FINISH_DT > T.ORDER_DUE_DT THEN T.PART_QTY ELSE 0 END) AS LATE_QTY , SUM(T.FORECAST_UNIT_AMT) AS FORECAST_UNIT_AMT , SUM(T.ERROR_CNT) AS ERROR_CNT , SUM(T.ERROR_QTY) AS ERROR_QTY , SUM(CASE WHEN T.RETURN_QTY > 0 THEN 1 ELSE 0 END) AS RETURN_CNT , SUM(T.RETURN_QTY) AS RETURN_QTY FROM ( SELECT P.CONTROL_SEQ , P.CONTROL_DETAIL_SEQ , P.ORDER_DUE_DT , P.PART_CNT , P.PART_QTY , P.OUTSIDE_YN , P.INNER_WORK_FINISH_YN , P.OUT_FINISH_YN , P.OUT_FINISH_DT , P.FORECAST_UNIT_AMT , P.ERROR_CNT , P.ERROR_QTY , ( SELECT SUM(M.ERROR_QTY) FROM jmes.TBL_INSPECT M WHERE M.CONTROL_SEQ = P.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = P.CONTROL_DETAIL_SEQ AND M.INSPECT_TYPE = '2' AND M.INSPECT_GRADE IN ('GRD050', 'GRD060') ) AS RETURN_QTY FROM ( SELECT C.CONTROL_SEQ , C.CONTROL_DETAIL_SEQ , A.ORDER_DUE_DT , 1 AS PART_CNT , jmes.SF_GET_CONTROL_PART_QTY(C.CONTROL_SEQ, C.CONTROL_DETAIL_SEQ) AS PART_QTY , C.OUTSIDE_YN , CASE WHEN C.INNER_WORK_FINISH_DT IS NULL THEN 'N' ELSE 'Y' END AS INNER_WORK_FINISH_YN , CASE WHEN B.OUT_FINISH_DT IS NULL THEN 'N' ELSE 'Y' END AS OUT_FINISH_YN , DATE_FORMAT(B.OUT_FINISH_DT,'%Y%m%d') AS OUT_FINISH_DT , C.FORECAST_UNIT_AMT , ROW_NUMBER() OVER (PARTITION BY C.CONTROL_SEQ, C.CONTROL_DETAIL_SEQ ORDER BY D.INSPECT_SEQ) AS INSPECT_RNUM , CASE WHEN D.INSPECT_GRADE = 'GRD040' THEN 1 ELSE 0 END AS ERROR_CNT , CASE WHEN D.INSPECT_GRADE = 'GRD040' THEN D.ERROR_QTY ELSE 0 END AS ERROR_QTY FROM ( SELECT M.CONTROL_SEQ , MAX(M.ORDER_DUE_DT) AS ORDER_DUE_DT FROM jmes.TBL_CONTROL_PART_ORDER M GROUP BY M.CONTROL_SEQ HAVING MAX(M.ORDER_DUE_DT) LIKE CONCAT(CONCAT(#{YEAR}, #{MONTH}),'%') ) A , jmes.TBL_CONTROL B , jmes.TBL_CONTROL_PART C LEFT OUTER JOIN jmes.TBL_INSPECT D ON C.CONTROL_SEQ = D.CONTROL_SEQ AND C.CONTROL_DETAIL_SEQ = D.CONTROL_DETAIL_SEQ AND D.INSPECT_TYPE = '1' WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_SEQ = C.CONTROL_SEQ AND B.DEL_YN = 'N' AND B.CONTROL_STATUS IN ('ORD001', 'ORD003') AND C.WORK_TYPE != 'WTP020'
AND B.COMP_CD = #{COMP_CD}
AND B.ORDER_COMP_CD = #{ORDER_COMP_CD}
) P WHERE P.INSPECT_RNUM = 1 ) T GROUP BY T.ORDER_DUE_DT ) Y ON X.DT = Y.ORDER_DUE_DT GROUP BY X.WEEK_SEQ, X.DT WITH ROLLUP ) Z ORDER BY Z.WEEK_SEQ IS NULL ASC, Z.WEEK_SEQ, Z.DT IS NULL ASC, Z.DT

SELECT SF_GET_COMP_NM(A.ORDER_COMP_CD) AS ORDER_COMP_NM , SF_GET_CODE_NM('1014', B.WORK_FACTORY, #{LOGIN_LOCALE}) AS WORK_FACTORY_NM , CASE WHEN B.PART_NUM IS NULL THEN A.CONTROL_NUM ELSE CONCAT(A.CONTROL_NUM, ' #', B.PART_NUM) END AS CONTROL_NUM , B.DRAWING_NUM , B.IMG_GFILE_SEQ , jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ) AS PART_QTY , jmes.SF_GET_DATE_F(B.INNER_WORK_FINISH_DT, '', 'M') AS INNER_WORK_FINISH_DT , (SELECT ROUND(SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK', M.MCT_WORK_SEQ)), 0) FROM jmes.TBL_MCT_WORK M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ) AS WORK_TIME , jmes.SF_GET_USER_NM(C.INSPECT_USER_ID) AS INSPECT_USER_NM , jmes.SF_GET_CODE_NM('1040', C.INSPECT_GRADE, #{LOGIN_LOCALE}) AS INSPECT_GRADE_NM , (SELECT NOTE FROM TBL_CODE WHERE CODE_CD = C.INSPECT_GRADE) AS INSPECT_GRADE_NOTE , C.ERROR_QTY , jmes.SF_GET_DATE_F(C.RETURN_SETTLEMENT_DT, '', 'M') AS RETURN_SETTLEMENT_DT , CASE C.INSPECT_TYPE WHEN '1' THEN SF_GET_CODE_NM('1020', C.INSPECT_RESULT, #{LOGIN_LOCALE}) WHEN '2' THEN SF_GET_CODE_NM('1021', C.INSPECT_RESULT, #{LOGIN_LOCALE}) END AS INSPECT_RESULT_NM , C.INSPECT_DESC , jmes.SF_GET_CODE_NM('1010', C.ERROR_PROCESS, #{LOGIN_LOCALE}) AS ERROR_PROCESS_NM , jmes.SF_GET_CODE_NM('1032', C.ERROR_REASON, #{LOGIN_LOCALE}) AS ERROR_REASON_NM , jmes.SF_GET_CODE_NM('1025', C.ERROR_ACTION, #{LOGIN_LOCALE}) AS ERROR_ACTION_NM , jmes.SF_GET_DATE_F(C.INSERT_DT, '', 'M') AS INSERT_DT FROM jmes.TBL_CONTROL A, jmes.TBL_CONTROL_PART B, jmes.TBL_INSPECT C WHERE 1 = 1 AND A.CONTROL_SEQ = B.CONTROL_SEQ AND B.CONTROL_SEQ = C.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND C.INSPECT_SEQ IN ( SELECT P.INSPECT_SEQ FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY C.CONTROL_SEQ, C.CONTROL_DETAIL_SEQ ORDER BY D.INSPECT_SEQ) AS INSPECT_RNUM , D.INSPECT_SEQ , D.INSPECT_GRADE , D.ERROR_QTY FROM ( SELECT M.CONTROL_SEQ , MAX(M.ORDER_DUE_DT) AS ORDER_DUE_DT FROM jmes.TBL_CONTROL_PART_ORDER M GROUP BY M.CONTROL_SEQ HAVING MAX(M.ORDER_DUE_DT) = #{DT} ) A , jmes.TBL_CONTROL B , jmes.TBL_CONTROL_PART C LEFT OUTER JOIN jmes.TBL_INSPECT D ON C.CONTROL_SEQ = D.CONTROL_SEQ AND C.CONTROL_DETAIL_SEQ = D.CONTROL_DETAIL_SEQ
AND D.INSPECT_TYPE = '1'
AND D.INSPECT_TYPE = '2'
WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_SEQ = C.CONTROL_SEQ AND B.DEL_YN = 'N' AND C.WORK_TYPE != 'WTP020'
AND B.COMP_CD = #{COMP_CD}
AND B.ORDER_COMP_CD = #{ORDER_COMP_CD}
) P WHERE P.INSPECT_RNUM = 1 AND P.ERROR_QTY > 0
AND P.INSPECT_GRADE = 'GRD040'
AND P.INSPECT_GRADE IN ('GRD050', 'GRD060')
)

mapper.update:
UPDATE TBL_WORKING_WEEK_TIME
SET
    WORKING_TIME = #{WORKING_TIME},
    UPDATE_DT = NOW(),
    UPDATE_ID = #{LOGIN_USER_ID}
WHERE 1 = 1
    AND WORK_FACTORY = #{WORK_FACTORY}
    AND WEEK_DAY = #{WEEK_DAY}

mapper.select:
SELECT Z.YYYYMM , Z.PART_CNT , Z.OK_CNT , Z.OK_RATIO , CASE WHEN IFNULL(Z.OK_RATIO,0) > IFNULL(Z.PREV_OK_RATIO,0) THEN '+' WHEN IFNULL(Z.OK_RATIO,0) < IFNULL(Z.PREV_OK_RATIO,0) THEN '-' ELSE '' END AS RATIO_SIGN , ABS(IFNULL(Z.OK_RATIO,0) - IFNULL(Z.PREV_OK_RATIO,0)) AS RATIO_GAP FROM ( SELECT MAX(CASE WHEN X.RNUM = 1 THEN X.YYYYMM END) AS YYYYMM , MAX(CASE WHEN X.RNUM = 1 THEN Y.PART_CNT END) AS PART_CNT , MAX(CASE WHEN X.RNUM = 1 THEN Y.OK_CNT END) AS OK_CNT , MAX(CASE WHEN X.RNUM = 1 THEN CASE WHEN IFNULL(Y.PART_CNT,0) = 0 THEN 0 ELSE ROUND(Y.OK_CNT / Y.PART_CNT * 100,0) END END) AS OK_RATIO , MAX(CASE WHEN X.RNUM = 2 THEN Y.PART_CNT END) AS PREV_PART_CNT , MAX(CASE WHEN X.RNUM = 2 THEN Y.OK_CNT END) AS PREV_OK_CNT , MAX(CASE WHEN X.RNUM = 2 THEN CASE WHEN IFNULL(Y.PART_CNT,0) = 0 THEN 0 ELSE ROUND(Y.OK_CNT / Y.PART_CNT * 100,0) END END) AS PREV_OK_RATIO FROM ( SELECT ROW_NUMBER() OVER(ORDER BY M.YYYYMM DESC) AS RNUM , M.YYYYMM FROM ( SELECT SUBSTRING(DT,1,6) AS YYYYMM FROM jmes.TBL_YYYYMMDD WHERE DT >= DATE_FORMAT(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL -1 MONTH),'%Y%m%d') AND DT < DATE_FORMAT(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL 1 MONTH),'%Y%m%d') GROUP BY SUBSTRING(DT,1,6) ) M ) X LEFT OUTER JOIN ( SELECT SUBSTRING(P.WORKING_FINISH_DT,1,6) AS YYYYMM , SUM(1) AS PART_CNT , SUM(CASE WHEN P.WORKING_FINISH_DT <= P.INNER_DUE_DT THEN 1 ELSE 0 END) AS OK_CNT FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ , DATE_FORMAT(DATE_ADD(B.INNER_WORK_FINISH_DT, INTERVAL -1 * (CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT)) MINUTE),'%Y%m%d') AS WORKING_FINISH_DT , B.INNER_DUE_DT FROM jmes.TBL_CONTROL A, jmes.TBL_CONTROL_PART B WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.DEL_YN = 'N' AND B.WORK_TYPE != 'WTP020' AND B.INNER_WORK_FINISH_DT >= DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE), INTERVAL -1 MONTH) AND B.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL 1 MONTH), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE)
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.WORK_FACTORY = #{WORK_FACTORY}
) P GROUP BY SUBSTRING(P.WORKING_FINISH_DT,1,6) ) Y ON X.YYYYMM = Y.YYYYMM ) Z

SELECT X.YYYYMM , FORMAT(IFNULL(Y.SALES_AMT,0) / 100000000,1) AS SALES_AMT , IFNULL(Y.OUTSIDE_CNT,0) AS OUTSIDE_CNT , IFNULL(Y.INSIDE_CNT,0) AS INSIDE_CNT FROM ( SELECT SUBSTRING(DT,1,6) AS YYYYMM FROM jmes.TBL_YYYYMMDD WHERE DT LIKE CONCAT(#{YEAR}, #{MONTH},'%') GROUP BY SUBSTRING(DT,1,6) ) X LEFT OUTER JOIN ( SELECT SUBSTRING(P.WORKING_FINISH_DT,1,6) AS YYYYMM , SUM(1) AS PART_CNT , SUM(CASE WHEN P.OUTSIDE_YN = 'Y' THEN 1 ELSE 0 END) AS OUTSIDE_CNT , SUM(CASE WHEN P.OUTSIDE_YN = 'Y' THEN 0 ELSE 1 END) AS INSIDE_CNT , SUM(P.FINAL_AMT) AS SALES_AMT FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ , DATE_FORMAT(DATE_ADD(B.INNER_WORK_FINISH_DT, INTERVAL -1 * (CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT)) MINUTE),'%Y%m%d') AS WORKING_FINISH_DT , B.OUTSIDE_YN , jmes.SF_GET_CONTROL_FINAL_AMT(B.CONTROL_SEQ) AS FINAL_AMT FROM jmes.TBL_CONTROL A, jmes.TBL_CONTROL_PART B WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.DEL_YN = 'N' AND B.WORK_TYPE != 'WTP020' AND B.INNER_WORK_FINISH_DT >= DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND B.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL 1 MONTH), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE)
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.WORK_FACTORY = #{WORK_FACTORY}
) P GROUP BY SUBSTRING(P.WORKING_FINISH_DT,1,6) ) Y ON X.YYYYMM = Y.YYYYMM

SELECT X.YYYYMM , SUBSTR(X.YYYYMM,5,2) AS MM , IFNULL(Y.GOAL_AMT,0) / 100000000 AS GOAL_AMT , IFNULL(Z.SALES_AMT,0) / 100000000 AS SALES_AMT , IFNULL(Z.OK_RATIO,0) AS OK_RATIO FROM ( SELECT SUBSTRING(DT,1,6) AS YYYYMM FROM jmes.TBL_YYYYMMDD WHERE DT LIKE CONCAT(#{YEAR},'%') GROUP BY SUBSTRING(DT,1,6) ) X LEFT OUTER JOIN ( SELECT YYYYMM, SUM(GOAL_AMT) AS GOAL_AMT FROM jmes.TBL_SALES_GOAL WHERE YYYYMM LIKE CONCAT(#{YEAR},'%')
AND WORK_FACTORY = #{WORK_FACTORY}
GROUP BY YYYYMM ) Y ON X.YYYYMM = Y.YYYYMM LEFT OUTER JOIN ( SELECT Q.YYYYMM , Q.PART_CNT , Q.OK_CNT , CASE WHEN IFNULL(Q.PART_CNT,0) = 0 THEN 0 ELSE ROUND(IFNULL(Q.OK_CNT,0) / IFNULL(Q.PART_CNT,0) * 100,0) END AS OK_RATIO , Q.SALES_AMT FROM ( SELECT SUBSTRING(P.WORKING_FINISH_DT,1,6) AS YYYYMM , SUM(1) AS PART_CNT , SUM(CASE WHEN P.WORKING_FINISH_DT <= P.INNER_DUE_DT THEN 1 ELSE 0 END) AS OK_CNT , SUM(P.FINAL_AMT) AS SALES_AMT FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ , DATE_FORMAT(DATE_ADD(B.INNER_WORK_FINISH_DT, INTERVAL -1 * (CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT)) MINUTE),'%Y%m%d') AS WORKING_FINISH_DT , B.INNER_DUE_DT , jmes.SF_GET_CONTROL_FINAL_AMT(B.CONTROL_SEQ) AS FINAL_AMT FROM jmes.TBL_CONTROL A, jmes.TBL_CONTROL_PART B WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.DEL_YN = 'N' AND B.WORK_TYPE != 'WTP020' AND B.INNER_WORK_FINISH_DT >= DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, '01','01'),'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND B.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, '12','01'),'%Y%m%d'), INTERVAL 1 MONTH), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE)
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.WORK_FACTORY = #{WORK_FACTORY}
) P GROUP BY SUBSTRING(P.WORKING_FINISH_DT,1,6) ) Q ) Z ON X.YYYYMM = Z.YYYYMM ORDER BY X.YYYYMM

SELECT Z.MATERIAL_TYPE , CASE WHEN Z.MATERIAL_TYPE IS NULL THEN '합계' WHEN Z.MATERIAL_TYPE = 'MTP999' THEN '기타' ELSE jmes.SF_GET_CODE_NM('1035', Z.MATERIAL_TYPE, #{LOGIN_LOCALE}) END AS MATERIAL_TYPE_NM , FORMAT(IFNULL(Z.PART_CNT,0),0) AS PART_CNT , FORMAT(IFNULL(Z.PART_QTY,0),0) AS PART_QTY , CASE WHEN Z.MATERIAL_TYPE IS NULL THEN 100 ELSE CASE WHEN IFNULL(Z.TOTAL_PART_QTY,0) = 0 THEN 0 ELSE ROUND(IFNULL(Z.PART_QTY,0) / Z.TOTAL_PART_QTY * 100,0) END END AS PART_QTY_RATIO , REPLACE(CONCAT(TRUNCATE(IFNULL(Z.WORK_TIME,0)/3600,0),'h ',TRUNCATE((IFNULL(Z.WORK_TIME,0)%3600)/60,0),'m'),'0h ',space(0)) AS WORK_TIME , CASE WHEN Z.MATERIAL_TYPE IS NULL THEN 100 ELSE CASE WHEN IFNULL(Z.TOTAL_WORK_TIME,0) = 0 THEN 0 ELSE ROUND(IFNULL(Z.WORK_TIME,0) / Z.TOTAL_WORK_TIME * 100,0) END END AS WORK_TIME_RATIO FROM ( SELECT X.MATERIAL_TYPE , SUM(X.PART_CNT) AS PART_CNT , SUM(X.PART_QTY) AS PART_QTY , SUM(X.WORK_TIME) AS WORK_TIME , MAX(X.TOTAL_PART_CNT) AS TOTAL_PART_CNT , MAX(X.TOTAL_PART_QTY) AS TOTAL_PART_QTY , MAX(X.TOTAL_WORK_TIME) AS TOTAL_WORK_TIME FROM ( SELECT SUBSTRING(P.WORKING_FINISH_DT,1,6) AS YYYYMM , P.MATERIAL_TYPE , SUM(1) AS PART_CNT , SUM(P.PART_QTY) AS PART_QTY , SUM(P.WORK_TIME) AS WORK_TIME , MAX(P.TOTAL_PART_CNT) AS TOTAL_PART_CNT , MAX(P.TOTAL_PART_QTY) AS TOTAL_PART_QTY , MAX(P.TOTAL_WORK_TIME) AS TOTAL_WORK_TIME FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ , DATE_FORMAT(DATE_ADD(B.INNER_WORK_FINISH_DT, INTERVAL -1 * (CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT)) MINUTE),'%Y%m%d') AS WORKING_FINISH_DT , CASE WHEN B.MATERIAL_TYPE IN ('MTP010','MTP020','MTP030') THEN B.MATERIAL_TYPE ELSE 'MTP999' END AS MATERIAL_TYPE , jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ) AS PART_QTY , (SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ) AS WORK_TIME , SUM(1) OVER () AS TOTAL_PART_CNT , SUM(jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ)) OVER () AS TOTAL_PART_QTY , SUM((SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ)) OVER () AS TOTAL_WORK_TIME FROM jmes.TBL_CONTROL A, jmes.TBL_CONTROL_PART B WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.DEL_YN = 'N' AND B.WORK_TYPE != 'WTP020' AND B.INNER_WORK_FINISH_DT >= DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND B.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL 1 MONTH), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE)
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.WORK_FACTORY = #{WORK_FACTORY}
) P GROUP BY SUBSTRING(P.WORKING_FINISH_DT,1,6), P.MATERIAL_TYPE ) X GROUP BY X.MATERIAL_TYPE WITH ROLLUP ) Z ORDER BY Z.MATERIAL_TYPE IS NULL ASC, Z.MATERIAL_TYPE

SELECT P.MATERIAL_TYPE , FORMAT(CASE WHEN Q.MATERIAL_TYPE IS NULL THEN P.PART_QTY ELSE Q.PART_QTY END,0) AS PART_QTY , REPLACE(CASE WHEN Q.MATERIAL_TYPE IS NULL THEN '0h 0m' ELSE CONCAT(TRUNCATE(IFNULL(Q.WORK_TIME,0)/3600,0),'h ',TRUNCATE((IFNULL(Q.WORK_TIME,0)%3600)/60,0),'m') END,'0h ',space(0)) AS WORK_TIME , FORMAT(CASE WHEN Q.MATERIAL_TYPE IS NULL THEN P.TOTAL_EC_AMT ELSE Q.TOTAL_EC_AMT END,0) AS TOTAL_EC_AMT , FORMAT(CASE WHEN Q.MATERIAL_TYPE IS NULL THEN P.TOTAL_AP_AMT ELSE Q.TOTAL_AP_AMT END,0) AS TOTAL_AP_AMT , FORMAT(CASE WHEN Q.MATERIAL_TYPE IS NULL THEN P.MARGIN_RATIO ELSE Q.MARGIN_RATIO END,1) AS MARGIN_RATIO FROM ( SELECT CASE WHEN CODE_CD IN ('MTP010','MTP020','MTP030') THEN CODE_CD ELSE 'MTP999' END AS MATERIAL_TYPE , 0 AS PART_QTY , '0h 0m' AS WORK_TIME , 0 AS TOTAL_EC_AMT , 0 AS TOTAL_AP_AMT , 0 AS MARGIN_RATIO FROM TBL_CODE WHERE HIGH_CD = '1035' AND CODE_CD IN ('MTP010','MTP020','MTP030','MTP040') ) P LEFT OUTER JOIN ( SELECT Z.MATERIAL_TYPE , Z.PART_QTY , Z.WORK_TIME , Z.TOTAL_EC_AMT , Z.TOTAL_AP_AMT , CASE WHEN Z.TOTAL_EC_AMT = 0 THEN 0 ELSE (Z.TOTAL_AP_AMT - Z.TOTAL_EC_AMT) / Z.TOTAL_EC_AMT * 100 END AS MARGIN_RATIO FROM ( SELECT X.MATERIAL_TYPE , IFNULL(SUM(X.PART_QTY),0) AS PART_QTY , CONCAT(TRUNCATE(IFNULL(SUM(X.WORK_TIME),0)/3600,0),'h ',TRUNCATE((IFNULL(SUM(X.WORK_TIME),0)%3600)/60,0),'m') AS WORK_TIME , TRUNCATE(SUM(IFNULL(X.UNIT_EC_AMT,0) * X.PART_QTY),0) AS TOTAL_EC_AMT , TRUNCATE(SUM(IFNULL(X.UNIT_AP_AMT,0) * X.PART_QTY),0) AS TOTAL_AP_AMT FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ , CASE WHEN B.MATERIAL_TYPE IN ('MTP010','MTP020','MTP030') THEN B.MATERIAL_TYPE ELSE 'MTP999' END AS MATERIAL_TYPE , jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ) AS PART_QTY , (SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ) AS WORK_TIME , ( SELECT SUM(IFNULL(N.COST_PER_HOUR,0) * jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ) / 3600 / jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ)) AS UNIT_EC_AMT FROM jmes.TBL_MCT_WORK M, jmes.TBL_EQUIP N WHERE M.EQUIP_SEQ = N.EQUIP_SEQ AND M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ ) AS UNIT_EC_AMT , (IFNULL(D.UNIT_FINAL_AMT,0) - (IFNULL(B.UNIT_MATERIAL_AMT,0) + IFNULL(B.UNIT_MATERIAL_FINISH_TM_AMT,0) + IFNULL(B.UNIT_MATERIAL_FINISH_GRIND_AMT,0) + IFNULL(B.UNIT_MATERIAL_FINISH_HEAT_AMT,0) + IFNULL(B.UNIT_SURFACE_AMT,0) + IFNULL(B.UNIT_PROCESS_AMT,0) + IFNULL(B.UNIT_ETC_AMT,0))) AS UNIT_AP_AMT , ROW_NUMBER() OVER (PARTITION BY B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ ORDER BY C.INSPECT_SEQ) AS INSPECT_RNUM , C.INSPECT_GRADE FROM jmes.TBL_CONTROL A , jmes.TBL_CONTROL_PART B LEFT OUTER JOIN jmes.TBL_INSPECT C ON B.CONTROL_SEQ = C.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND C.INSPECT_TYPE = '1' , jmes.TBL_CONTROL_PART_ORDER_MAIN D WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_SEQ = D.CONTROL_SEQ AND A.DEL_YN = 'N' AND B.WORK_TYPE != 'WTP020' AND B.INNER_WORK_FINISH_DT >= DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND B.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL 1 MONTH), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE)
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.WORK_FACTORY = #{WORK_FACTORY}
) X WHERE INSPECT_RNUM = 1
AND X.PART_QTY >= ${YY1}
AND X.PART_QTY <= ${YY2}
AND X.INSPECT_GRADE IN (${HIDDEN_INSPECT_GRADE})
GROUP BY X.MATERIAL_TYPE ) Z ) Q ON P.MATERIAL_TYPE = Q.MATERIAL_TYPE ORDER BY P.MATERIAL_TYPE

SELECT X.CONTROL_SEQ, X.CONTROL_DETAIL_SEQ , CASE WHEN X.PART_NUM IS NULL THEN X.CONTROL_NUM ELSE CONCAT(X.CONTROL_NUM, ' #', X.PART_NUM) END AS CONTROL_NUM , X.PART_QTY , REPLACE(CONCAT(TRUNCATE(IFNULL(X.WORK_TIME,0)/3600,0),'h ',TRUNCATE((IFNULL(X.WORK_TIME,0)%3600)/60,0),'m'),'0h ',space(0)) AS WORK_TIME , REPLACE(CONCAT(TRUNCATE(IFNULL(X.WORK_TIME_NC,0)/3600,0),'h ',TRUNCATE((IFNULL(X.WORK_TIME_NC,0)%3600)/60,0),'m'),'0h ',space(0)) AS WORK_TIME_NC , REPLACE(CONCAT(TRUNCATE(IFNULL(X.WORK_TIME_MILLING,0)/3600,0),'h ',TRUNCATE((IFNULL(X.WORK_TIME_MILLING,0)%3600)/60,0),'m'),'0h ',space(0)) AS WORK_TIME_MILLING , REPLACE(CONCAT(TRUNCATE(IFNULL(X.WORK_TIME_GRINDING,0)/3600,0),'h ',TRUNCATE((IFNULL(X.WORK_TIME_GRINDING,0)%3600)/60,0),'m'),'0h ',space(0)) AS WORK_TIME_GRINDING , REPLACE(CONCAT(TRUNCATE(IFNULL(X.WORK_TIME_TURNNING,0)/3600,0),'h ',TRUNCATE((IFNULL(X.WORK_TIME_TURNNING,0)%3600)/60,0),'m'),'0h ',space(0)) AS WORK_TIME_TURNNING , X.UNIT_EC_AMT, FORMAT(X.UNIT_EC_AMT,0) AS UNIT_EC_AMT_FMT , X.UNIT_AP_AMT, FORMAT(X.UNIT_AP_AMT,0) AS UNIT_AP_AMT_FMT , FORMAT(CASE WHEN IFNULL(X.UNIT_EC_AMT,0) = 0 THEN NULL ELSE (IFNULL(X.UNIT_AP_AMT,0) - X.UNIT_EC_AMT) / X.UNIT_EC_AMT * 100 END,1) AS MARGIN_RATIO , X.SIZE_TXT , jmes.SF_GET_CODE_NM('1027',X.MATERIAL_DETAIL,#{LOGIN_LOCALE}) AS MATERIAL_DETAIL_NM -- 언어조건 , jmes.SF_GET_CODE_NM('1040',X.INSPECT_GRADE,#{LOGIN_LOCALE}) AS INSPECT_GRADE_NM , CASE WHEN X.INSPECT_GRADE = 'GRD040' THEN jmes.SF_GET_CODE_NM('1020',X.INSPECT_RESULT,#{LOGIN_LOCALE}) ELSE jmes.SF_GET_CODE_NM('1019',X.INSPECT_RESULT,#{LOGIN_LOCALE}) END AS INSPECT_RESULT_NM , jmes.SF_GET_CODE_NM('1032',X.ERROR_REASON,#{LOGIN_LOCALE}) AS ERROR_REASON_NM , jmes.SF_GET_CODE_NM('1025',X.ERROR_ACTION,#{LOGIN_LOCALE}) AS ERROR_ACTION_NM , ( SELECT jmes.SF_GET_USER_NM(B.WORK_USER_ID) FROM jmes.TBL_CAM A, jmes.TBL_CAM_DETAIL B WHERE A.CAM_SEQ = B.CAM_SEQ AND A.CONTROL_SEQ = X.CONTROL_SEQ AND A.CONTROL_DETAIL_SEQ = X.CONTROL_DETAIL_SEQ ORDER BY B.CAM_SEQ DESC, B.SEQ DESC LIMIT 1) AS CAM_USER_NM
, X.IMG_GFILE_SEQ FROM ( SELECT B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ , B.PART_NUM, A.CONTROL_NUM, B.SIZE_TXT, B.MATERIAL_DETAIL, B.IMG_GFILE_SEQ , jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ) AS PART_QTY , (SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M WHERE M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ) AS WORK_TIME , (SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M, jmes.TBL_EQUIP N WHERE M.EQUIP_SEQ = N.EQUIP_SEQ AND M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND N.PROCESS_TYPE = 'MPR010') AS WORK_TIME_NC , (SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M, jmes.TBL_EQUIP N WHERE M.EQUIP_SEQ = N.EQUIP_SEQ AND M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND N.PROCESS_TYPE = 'MPR020') AS WORK_TIME_MILLING , (SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M, jmes.TBL_EQUIP N WHERE M.EQUIP_SEQ = N.EQUIP_SEQ AND M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND N.PROCESS_TYPE = 'MPR030') AS WORK_TIME_GRINDING , (SELECT SUM(jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ)) FROM jmes.TBL_MCT_WORK M, jmes.TBL_EQUIP N WHERE M.EQUIP_SEQ = N.EQUIP_SEQ AND M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ AND N.PROCESS_TYPE = 'MPR040') AS WORK_TIME_TURNNING , ( SELECT TRUNCATE(SUM(IFNULL(N.COST_PER_HOUR,0) * jmes.SF_GET_DRAWING_WORK_TIME('WORK',M.MCT_WORK_SEQ) / 3600 / jmes.SF_GET_CONTROL_PART_QTY(B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ)),0) FROM jmes.TBL_MCT_WORK M, jmes.TBL_EQUIP N WHERE M.EQUIP_SEQ = N.EQUIP_SEQ AND M.CONTROL_SEQ = B.CONTROL_SEQ AND M.CONTROL_DETAIL_SEQ = B.CONTROL_DETAIL_SEQ ) AS UNIT_EC_AMT , (IFNULL(D.UNIT_FINAL_AMT,0) - (IFNULL(B.UNIT_MATERIAL_AMT,0) + IFNULL(B.UNIT_MATERIAL_FINISH_TM_AMT,0) + IFNULL(B.UNIT_MATERIAL_FINISH_GRIND_AMT,0) + IFNULL(B.UNIT_MATERIAL_FINISH_HEAT_AMT,0) + IFNULL(B.UNIT_SURFACE_AMT,0) + IFNULL(B.UNIT_PROCESS_AMT,0) + IFNULL(B.UNIT_ETC_AMT,0))) AS UNIT_AP_AMT , ROW_NUMBER() OVER (PARTITION BY B.CONTROL_SEQ, B.CONTROL_DETAIL_SEQ ORDER BY C.INSPECT_SEQ) AS INSPECT_RNUM , C.INSPECT_GRADE , C.INSPECT_RESULT , C.ERROR_REASON , C.ERROR_ACTION FROM jmes.TBL_CONTROL A , jmes.TBL_CONTROL_PART B LEFT OUTER JOIN jmes.TBL_INSPECT C ON B.CONTROL_SEQ = C.CONTROL_SEQ AND B.CONTROL_DETAIL_SEQ = C.CONTROL_DETAIL_SEQ AND C.INSPECT_TYPE = '1' , jmes.TBL_CONTROL_PART_ORDER_MAIN D WHERE A.CONTROL_SEQ = B.CONTROL_SEQ AND A.CONTROL_SEQ = D.CONTROL_SEQ AND A.DEL_YN = 'N' AND B.WORK_TYPE != 'WTP020'
AND B.MATERIAL_TYPE = #{MATERIAL_TYPE}
AND B.MATERIAL_TYPE NOT IN (${MATERIAL_TYPE})
AND B.INNER_WORK_FINISH_DT >= DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE) AND B.INNER_WORK_FINISH_DT < DATE_ADD(DATE_ADD(STR_TO_DATE(CONCAT(#{YEAR}, #{MONTH},'01'),'%Y%m%d'), INTERVAL 1 MONTH), INTERVAL CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),1,2) AS INT) * 60 + CAST(SUBSTRING(SF_GET_CODE_NM('1086','1',#{LOGIN_LOCALE}),3,2) AS INT) MINUTE)
AND A.ORDER_COMP_CD = #{ORDER_COMP_CD}
AND B.WORK_FACTORY = #{WORK_FACTORY}
AND B.SIZE_W >= #{SIZE_W_F}
AND B.SIZE_W <= #{SIZE_W_T}
AND B.SIZE_H >= #{SIZE_H_F}
AND B.SIZE_H <= #{SIZE_H_T}
AND B.SIZE_T >= #{SIZE_T_F}
AND B.SIZE_T <= #{SIZE_T_T}
AND B.SIZE_D >= #{SIZE_D_F}
AND B.SIZE_D <= #{SIZE_D_T}
AND B.SIZE_L >= #{SIZE_L_F}
AND B.SIZE_L <= #{SIZE_L_T}
) X WHERE INSPECT_RNUM = 1
AND X.PART_QTY >= ${YY1}
AND X.PART_QTY <= ${YY2}
AND X.INSPECT_GRADE IN (${HIDDEN_INSPECT_GRADE})
ORDER BY ${ANALYSIS_TARGET} ${SORT}, X.CONTROL_SEQ DESC LIMIT 20

SELECT IFNULL(ROUND(SUM(A.WORK_TIME) / (24 * 60 * 60),0),0) AS REAL_RATIO
FROM jmes.TBL_BATCH_MCT_WORK A
WHERE A.BATCH_DT = DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')

    AND A.WORK_FACTORY = #{WORK_FACTORY}


    AND A.FACTORY_AREA = #{FACTORY_AREA}


    AND A.PROCESS_TYPE = #{PROCESS_TYPE}


    AND A.EQUIP_SEQ = #{EQUIP_SEQ}

SELECT X.EQUIP_SEQ , (SELECT M.EQUIP_NM FROM jmes.TBL_EQUIP M WHERE M.EQUIP_SEQ = X.EQUIP_SEQ) AS EQUIP_NM , ROUND(X.ABS_RATIO,0) AS ABS_RATIO , ROUND(X.REAL_RATIO,0) AS REAL_RATIO FROM ( SELECT A.EQUIP_SEQ , IFNULL(A.WORK_TIME,0) AS WORK_TIME , IFNULL(A.DUTY_TIME,0) AS DUTY_TIME , 24 * 60 * 60 AS DAY_TIME , IFNULL(A.WORK_TIME,0) / (24 * 60 * 60) * 100 AS ABS_RATIO , CASE WHEN IFNULL(A.DUTY_TIME,0) = 0 THEN 0 ELSE IFNULL(A.WORK_TIME,0) / A.DUTY_TIME * 100 END AS REAL_RATIO FROM jmes.TBL_BATCH_MCT_WORK A WHERE A.BATCH_DT = DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')
AND A.WORK_FACTORY = #{WORK_FACTORY}
AND A.FACTORY_AREA = #{FACTORY_AREA}
AND A.PROCESS_TYPE = #{PROCESS_TYPE}
AND A.EQUIP_SEQ = #{EQUIP_SEQ}
) X ORDER BY (X.ABS_RATIO + X.REAL_RATIO) DESC LIMIT 5

SELECT X.DATA_TYPE, X.DATA_TYPE_NM , MAX(CASE WHEN X.MM = '01' THEN X.DATA_VALUE END) AS DATA_VALUE_01 , MAX(CASE WHEN X.MM = '02' THEN X.DATA_VALUE END) AS DATA_VALUE_02 , MAX(CASE WHEN X.MM = '03' THEN X.DATA_VALUE END) AS DATA_VALUE_03 , MAX(CASE WHEN X.MM = '04' THEN X.DATA_VALUE END) AS DATA_VALUE_04 , MAX(CASE WHEN X.MM = '05' THEN X.DATA_VALUE END) AS DATA_VALUE_05 , MAX(CASE WHEN X.MM = '06' THEN X.DATA_VALUE END) AS DATA_VALUE_06 , MAX(CASE WHEN X.MM = '07' THEN X.DATA_VALUE END) AS DATA_VALUE_07 , MAX(CASE WHEN X.MM = '08' THEN X.DATA_VALUE END) AS DATA_VALUE_08 , MAX(CASE WHEN X.MM = '09' THEN X.DATA_VALUE END) AS DATA_VALUE_09 , MAX(CASE WHEN X.MM = '10' THEN X.DATA_VALUE END) AS DATA_VALUE_10 , MAX(CASE WHEN X.MM = '11' THEN X.DATA_VALUE END) AS DATA_VALUE_11 , MAX(CASE WHEN X.MM = '12' THEN X.DATA_VALUE END) AS DATA_VALUE_12 FROM ( SELECT A.DATA_TYPE , A.DATA_TYPE_NM , A.MM , CASE WHEN A.DATA_TYPE = '1' THEN ROUND(B.ABS_MM_TIME / 60 / 60,0) WHEN A.DATA_TYPE = '2' THEN ROUND(B.DUTY_TIME / 60 / 60,0) WHEN A.DATA_TYPE = '3' THEN ROUND(B.WORK_TIME / 60 / 60,0) WHEN A.DATA_TYPE = '4' THEN ROUND(IFNULL(B.WORK_TIME,0) / B.ABS_MM_TIME * 100,0) WHEN A.DATA_TYPE = '5' THEN ROUND(CASE WHEN IFNULL(B.DUTY_TIME,0) = 0 THEN 0 ELSE IFNULL(B.WORK_TIME,0) / B.DUTY_TIME * 100 END,0) END AS DATA_VALUE FROM ( SELECT Q.DATA_TYPE, Q.DATA_TYPE_NM , P.MM FROM ( SELECT M.MM FROM jmes.TBL_YYYYMMDD M WHERE M.DT LIKE CONCAT(SUBSTRING(DATE_FORMAT(#{BATCH_DT}, '%Y%m%d'),1,4),'%') GROUP BY M.MM ) P , ( SELECT '1' AS DATA_TYPE, '24시간' AS DATA_TYPE_NM FROM DUAL UNION ALL SELECT '2' AS DATA_TYPE, '근무시간' AS DATA_TYPE_NM FROM DUAL UNION ALL SELECT '3' AS DATA_TYPE, '가동시간' AS DATA_TYPE_NM FROM DUAL UNION ALL SELECT '4' AS DATA_TYPE, '절대가동률' AS DATA_TYPE_NM FROM DUAL UNION ALL SELECT '5' AS DATA_TYPE, '실가동률' AS DATA_TYPE_NM FROM DUAL ) Q ) A LEFT OUTER JOIN ( SELECT SUBSTRING(M.BATCH_DT,5,2) AS MM , SUM(M.WORK_TIME) AS WORK_TIME , SUM(M.DUTY_TIME) AS DUTY_TIME , SUM(24 * 60 * 60) AS ABS_MM_TIME FROM jmes.TBL_BATCH_MCT_WORK M WHERE M.BATCH_DT LIKE CONCAT(SUBSTRING(DATE_FORMAT(#{BATCH_DT}, '%Y%m%d'),1,4),'%')
AND M.WORK_FACTORY = #{WORK_FACTORY}
AND M.FACTORY_AREA = #{FACTORY_AREA}
AND M.PROCESS_TYPE = #{PROCESS_TYPE}
AND M.EQUIP_SEQ = #{EQUIP_SEQ}
GROUP BY SUBSTRING(M.BATCH_DT,5,2) ) B ON A.MM = B.MM ) X GROUP BY X.DATA_TYPE, X.DATA_TYPE_NM ORDER BY X.DATA_TYPE

SELECT X.DATA_TYPE, X.DATA_TYPE_NM , MAX(CASE WHEN X.RNUM = 1 THEN CASE WHEN X.DATA_TYPE = '1' THEN X.ABS_RATIO ELSE X.REAL_RATIO END END) DATA_VALUE_1 , MAX(CASE WHEN X.RNUM = 2 THEN CASE WHEN X.DATA_TYPE = '1' THEN X.ABS_RATIO ELSE X.REAL_RATIO END END) DATA_VALUE_2 , MAX(CASE WHEN X.RNUM = 3 THEN CASE WHEN X.DATA_TYPE = '1' THEN X.ABS_RATIO ELSE X.REAL_RATIO END END) DATA_VALUE_3 , MAX(CASE WHEN X.RNUM = 4 THEN CASE WHEN X.DATA_TYPE = '1' THEN X.ABS_RATIO ELSE X.REAL_RATIO END END) DATA_VALUE_4 , MAX(CASE WHEN X.RNUM = 5 THEN CASE WHEN X.DATA_TYPE = '1' THEN X.ABS_RATIO ELSE X.REAL_RATIO END END) DATA_VALUE_5 , MAX(CASE WHEN X.RNUM = 6 THEN CASE WHEN X.DATA_TYPE = '1' THEN X.ABS_RATIO ELSE X.REAL_RATIO END END) DATA_VALUE_6 , MAX(CASE WHEN X.RNUM = 7 THEN CASE WHEN X.DATA_TYPE = '1' THEN X.ABS_RATIO ELSE X.REAL_RATIO END END) DATA_VALUE_7 FROM ( SELECT A.DATA_TYPE, A.DATA_TYPE_NM, A.RNUM, A.DT , IFNULL(ROUND(IFNULL(B.WORK_TIME,0) / B.ABS_TIME * 100,0),0) AS ABS_RATIO , IFNULL(ROUND(CASE WHEN IFNULL(B.DUTY_TIME,0) = 0 THEN 0 ELSE IFNULL(B.WORK_TIME,0) / B.DUTY_TIME * 100 END,0),0) AS REAL_RATIO FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY N.DATA_TYPE ORDER BY M.DT) AS RNUM , M.DT , N.DATA_TYPE, N.DATA_TYPE_NM FROM jmes.TBL_YYYYMMDD M , ( SELECT '1' AS DATA_TYPE, '절대가동률' AS DATA_TYPE_NM FROM DUAL UNION ALL SELECT '2' AS DATA_TYPE, '실가동률' AS DATA_TYPE_NM FROM DUAL ) N WHERE M.DT BETWEEN DATE_FORMAT(DATE_ADD(STR_TO_DATE(DATE_FORMAT(#{BATCH_DT}, '%Y%m%d'),'%Y%m%d'), INTERVAL -6 DAY),'%Y%m%d') AND DATE_FORMAT(#{BATCH_DT}, '%Y%m%d') ) A LEFT OUTER JOIN ( SELECT M.BATCH_DT , SUM(M.WORK_TIME) AS WORK_TIME , SUM(M.DUTY_TIME) AS DUTY_TIME , SUM(24 * 60 * 60) AS ABS_TIME FROM jmes.TBL_BATCH_MCT_WORK M WHERE M.BATCH_DT BETWEEN DATE_FORMAT(DATE_ADD(STR_TO_DATE(DATE_FORMAT(#{BATCH_DT}, '%Y%m%d'),'%Y%m%d'), INTERVAL -6 DAY),'%Y%m%d') AND DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')
AND M.WORK_FACTORY = #{WORK_FACTORY}
AND M.FACTORY_AREA = #{FACTORY_AREA}
AND M.PROCESS_TYPE = #{PROCESS_TYPE}
AND M.EQUIP_SEQ = #{EQUIP_SEQ}
GROUP BY M.BATCH_DT ) B ON A.DT = B.BATCH_DT ) X GROUP BY X.DATA_TYPE, X.DATA_TYPE_NM

SELECT Z.DATA_TYPE, Z.DATA_TYPE_NM , MAX(CASE WHEN Z.WEEK_SEQ = 1 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_1 , MAX(CASE WHEN Z.WEEK_SEQ = 2 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_2 , MAX(CASE WHEN Z.WEEK_SEQ = 3 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_3 , MAX(CASE WHEN Z.WEEK_SEQ = 4 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_4 , MAX(CASE WHEN Z.WEEK_SEQ = 5 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_5 , MAX(CASE WHEN Z.WEEK_SEQ = 6 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_6 , MAX(CASE WHEN Z.WEEK_SEQ = 7 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_7 , MAX(CASE WHEN Z.WEEK_SEQ = 8 THEN CASE WHEN Z.DATA_TYPE = '1' THEN Z.ABS_RATIO ELSE Z.REAL_RATIO END END) DATA_VALUE_8 FROM ( SELECT X.DATA_TYPE, X.DATA_TYPE_NM, X.WEEK_SEQ , IFNULL(ROUND(IFNULL(X.WORK_TIME,0) / X.ABS_TIME * 100,0),0) AS ABS_RATIO , IFNULL(ROUND(CASE WHEN IFNULL(X.DUTY_TIME,0) = 0 THEN 0 ELSE IFNULL(X.WORK_TIME,0) / X.DUTY_TIME * 100 END,0),0) AS REAL_RATIO FROM ( SELECT A.DATA_TYPE, A.DATA_TYPE_NM , (TRUNCATE((A.RNUM -1)/7,0)+1) AS WEEK_SEQ , SUM(B.WORK_TIME) AS WORK_TIME , SUM(B.DUTY_TIME) AS DUTY_TIME , SUM(B.ABS_TIME) AS ABS_TIME FROM ( SELECT M.DT , ROW_NUMBER() OVER (PARTITION BY N.DATA_TYPE ORDER BY M.DT) AS RNUM , N.DATA_TYPE, N.DATA_TYPE_NM FROM jmes.TBL_YYYYMMDD M , ( SELECT '1' AS DATA_TYPE, '절대가동률' AS DATA_TYPE_NM FROM DUAL UNION ALL SELECT '2' AS DATA_TYPE, '실가동률' AS DATA_TYPE_NM FROM DUAL ) N WHERE M.DT >= ( SELECT DATE_FORMAT(DATE_ADD(STR_TO_DATE(MIN(M.DT),'%Y%m%d'), INTERVAL -55 DAY),'%Y%m%d') AS START_DT FROM jmes.TBL_YYYYMMDD M WHERE M.DT >= DATE_FORMAT(#{BATCH_DT}, '%Y%m%d') AND DAYOFWEEK(STR_TO_DATE(M.DT,'%Y%m%d')) = '1' ) AND M.DT <= ( SELECT MIN(M.DT) AS END_DT FROM jmes.TBL_YYYYMMDD M WHERE M.DT >= DATE_FORMAT(#{BATCH_DT}, '%Y%m%d') AND DAYOFWEEK(STR_TO_DATE(M.DT,'%Y%m%d')) = '1' ) ) A LEFT OUTER JOIN ( SELECT M.BATCH_DT , SUM(M.WORK_TIME) AS WORK_TIME , SUM(M.DUTY_TIME) AS DUTY_TIME , SUM(24 * 60 * 60) AS ABS_TIME FROM jmes.TBL_BATCH_MCT_WORK M WHERE M.BATCH_DT BETWEEN DATE_FORMAT(DATE_ADD(STR_TO_DATE(DATE_FORMAT(#{BATCH_DT}, '%Y%m%d'),'%Y%m%d'), INTERVAL -3 MONTH),'%Y%m%d') AND DATE_FORMAT(DATE_ADD(STR_TO_DATE(DATE_FORMAT(#{BATCH_DT}, '%Y%m%d'),'%Y%m%d'), INTERVAL 1 WEEK),'%Y%m%d')
AND M.WORK_FACTORY = #{WORK_FACTORY}
AND M.FACTORY_AREA = #{FACTORY_AREA}
AND M.PROCESS_TYPE = #{PROCESS_TYPE}
AND M.EQUIP_SEQ = #{EQUIP_SEQ}
GROUP BY M.BATCH_DT ) B ON A.DT = B.BATCH_DT GROUP BY A.DATA_TYPE, A.DATA_TYPE_NM, (TRUNCATE((A.RNUM -1)/7,0)+1) ) X ) Z GROUP BY Z.DATA_TYPE, Z.DATA_TYPE_NM

SELECT X.EQUIP_SEQ , SF_GET_CODE_NM('1014', X.WORK_FACTORY, 'KR') AS WORK_FACTORY_NM , jmes.SF_GET_CODE_NM('1005', X.FACTORY_AREA, 'KR') AS FACTORY_AREA_NM , jmes.SF_GET_CODE_NM('1010', X.PROCESS_TYPE, 'KR') AS PROCESS_TYPE_NM , (SELECT M.EQUIP_NM FROM jmes.TBL_EQUIP M WHERE M.EQUIP_SEQ = X.EQUIP_SEQ) AS EQUIP_NM , X.EQUIP_SIZE , REPLACE(CONCAT(TRUNCATE(IFNULL(X.WORK_TIME,0)/3600,0),'h ',TRUNCATE((IFNULL(X.WORK_TIME,0)%3600)/60,0),'m'),'0h ',space(0)) AS WORK_TIME , REPLACE(CONCAT(TRUNCATE(IFNULL(X.PAUSE_TIME,0)/3600,0),'h ',TRUNCATE((IFNULL(X.PAUSE_TIME,0)%3600)/60,0),'m'),'0h ',space(0)) AS PAUSE_TIME , REPLACE(CONCAT(TRUNCATE(IFNULL(X.REPAIR_TIME,0)/3600,0),'h ',TRUNCATE((IFNULL(X.REPAIR_TIME,0)%3600)/60,0),'m'),'0h ',space(0)) AS REPAIR_TIME , IFNULL(ROUND(CASE WHEN IFNULL(X.DUTY_TIME,0) = 0 THEN 0 ELSE IFNULL(X.WORK_TIME,0) / X.DUTY_TIME * 100 END,0),0) AS REAL_RATIO , Y.* FROM jmes.TBL_BATCH_MCT_WORK X , ( SELECT A.EQUIP_SEQ , MAX(CASE WHEN B.HHMM = '0800' THEN B.TIME_STATUS END) AS STATUS_0800 , MAX(CASE WHEN B.HHMM = '0830' THEN B.TIME_STATUS END) AS STATUS_0830 , MAX(CASE WHEN B.HHMM = '0900' THEN B.TIME_STATUS END) AS STATUS_0900 , MAX(CASE WHEN B.HHMM = '0930' THEN B.TIME_STATUS END) AS STATUS_0930 , MAX(CASE WHEN B.HHMM = '1000' THEN B.TIME_STATUS END) AS STATUS_1000 , MAX(CASE WHEN B.HHMM = '1030' THEN B.TIME_STATUS END) AS STATUS_1030 , MAX(CASE WHEN B.HHMM = '1100' THEN B.TIME_STATUS END) AS STATUS_1100 , MAX(CASE WHEN B.HHMM = '1130' THEN B.TIME_STATUS END) AS STATUS_1130 , MAX(CASE WHEN B.HHMM = '1200' THEN B.TIME_STATUS END) AS STATUS_1200 , MAX(CASE WHEN B.HHMM = '1230' THEN B.TIME_STATUS END) AS STATUS_1230 , MAX(CASE WHEN B.HHMM = '1300' THEN B.TIME_STATUS END) AS STATUS_1300 , MAX(CASE WHEN B.HHMM = '1330' THEN B.TIME_STATUS END) AS STATUS_1330 , MAX(CASE WHEN B.HHMM = '1400' THEN B.TIME_STATUS END) AS STATUS_1400 , MAX(CASE WHEN B.HHMM = '1430' THEN B.TIME_STATUS END) AS STATUS_1430 , MAX(CASE WHEN B.HHMM = '1500' THEN B.TIME_STATUS END) AS STATUS_1500 , MAX(CASE WHEN B.HHMM = '1530' THEN B.TIME_STATUS END) AS STATUS_1530 , MAX(CASE WHEN B.HHMM = '1600' THEN B.TIME_STATUS END) AS STATUS_1600 , MAX(CASE WHEN B.HHMM = '1630' THEN B.TIME_STATUS END) AS STATUS_1630 , MAX(CASE WHEN B.HHMM = '1700' THEN B.TIME_STATUS END) AS STATUS_1700 , MAX(CASE WHEN B.HHMM = '1730' THEN B.TIME_STATUS END) AS STATUS_1730 , MAX(CASE WHEN B.HHMM = '1800' THEN B.TIME_STATUS END) AS STATUS_1800 , MAX(CASE WHEN B.HHMM = '1830' THEN B.TIME_STATUS END) AS STATUS_1830 , MAX(CASE WHEN B.HHMM = '1900' THEN B.TIME_STATUS END) AS STATUS_1900 , MAX(CASE WHEN B.HHMM = '1930' THEN B.TIME_STATUS END) AS STATUS_1930 , MAX(CASE WHEN B.HHMM = '2000' THEN B.TIME_STATUS END) AS STATUS_2000 , MAX(CASE WHEN B.HHMM = '2030' THEN B.TIME_STATUS END) AS STATUS_2030 , MAX(CASE WHEN B.HHMM = '2100' THEN B.TIME_STATUS END) AS STATUS_2100 , MAX(CASE WHEN B.HHMM = '2130' THEN B.TIME_STATUS END) AS STATUS_2130 , MAX(CASE WHEN B.HHMM = '2200' THEN B.TIME_STATUS END) AS STATUS_2200 , MAX(CASE WHEN B.HHMM = '2230' THEN B.TIME_STATUS END) AS STATUS_2230 , MAX(CASE WHEN B.HHMM = '2300' THEN B.TIME_STATUS END) AS STATUS_2300 , MAX(CASE WHEN B.HHMM = '2330' THEN B.TIME_STATUS END) AS STATUS_2330 , MAX(CASE WHEN B.HHMM = '0000' THEN B.TIME_STATUS END) AS STATUS_0000 , MAX(CASE WHEN B.HHMM = '0030' THEN B.TIME_STATUS END) AS STATUS_0030 , MAX(CASE WHEN B.HHMM = '0100' THEN B.TIME_STATUS END) AS STATUS_0100 , MAX(CASE WHEN B.HHMM = '0130' THEN B.TIME_STATUS END) AS STATUS_0130 , MAX(CASE WHEN B.HHMM = '0200' THEN B.TIME_STATUS END) AS STATUS_0200 , MAX(CASE WHEN B.HHMM = '0230' THEN B.TIME_STATUS END) AS STATUS_0230 , MAX(CASE WHEN B.HHMM = '0300' THEN B.TIME_STATUS END) AS STATUS_0300 , MAX(CASE WHEN B.HHMM = '0330' THEN B.TIME_STATUS END) AS STATUS_0330 , MAX(CASE WHEN B.HHMM = '0400' THEN B.TIME_STATUS END) AS STATUS_0400 , MAX(CASE WHEN B.HHMM = '0430' THEN B.TIME_STATUS END) AS STATUS_0430 , MAX(CASE WHEN B.HHMM = '0500' THEN B.TIME_STATUS END) AS STATUS_0500 , MAX(CASE WHEN B.HHMM = '0530' THEN B.TIME_STATUS END) AS STATUS_0530 , MAX(CASE WHEN B.HHMM = '0600' THEN B.TIME_STATUS END) AS STATUS_0600 , MAX(CASE WHEN B.HHMM = '0630' THEN B.TIME_STATUS END) AS STATUS_0630 , MAX(CASE WHEN B.HHMM = '0700' THEN B.TIME_STATUS END) AS STATUS_0700 , MAX(CASE WHEN B.HHMM = '0730' THEN B.TIME_STATUS END) AS STATUS_0730 FROM jmes.TBL_BATCH_MCT_WORK A, TBL_BATCH_MCT_WORK_TIME_STATUS B WHERE A.BATCH_DT = B.BATCH_DT AND A.EQUIP_SEQ = B.EQUIP_SEQ AND A.BATCH_DT = DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')
AND A.WORK_FACTORY = #{WORK_FACTORY}
AND A.FACTORY_AREA = #{FACTORY_AREA}
AND A.PROCESS_TYPE = #{PROCESS_TYPE}
AND A.EQUIP_SEQ = #{EQUIP_SEQ}
GROUP BY A.EQUIP_SEQ ) Y WHERE X.EQUIP_SEQ = Y.EQUIP_SEQ AND X.BATCH_DT = DATE_FORMAT(#{BATCH_DT}, '%Y%m%d')
AND X.WORK_FACTORY = #{WORK_FACTORY}
AND X.FACTORY_AREA = #{FACTORY_AREA}
AND X.PROCESS_TYPE = #{PROCESS_TYPE}
AND X.EQUIP_SEQ = #{EQUIP_SEQ}
ORDER BY X.EQUIP_SEQ
