mapper.delete:
DELETE FROM TBL_USER
WHERE USER_ID = #{USER_ID}
     AND USER_NM = #{USER_NM}

mapper.insert:
INSERT INTO TBL_CODE ( HIGH_CD, CODE_CD, REF_CD, SORT_NUM, DEL_YN, NOTE, INSERT_DT
) VALUES( #{HIGH_CD}, #{CODE_CD}, #{REF_CD}, #{SORT_NUM}, #{DEL_YN}, #{NOTE}, NOW()
)

INSERT INTO TBL_CODE_LANG ( HIGH_CD, CODE_CD, LANG_CD, CODE_NM, INSERT_DT
) VALUES ( #{HIGH_CD}, #{CODE_CD}, #{LANG_CD}, #{CODE_NM}, NOW()
)

mapper.update:
INSERT INTO TBL_CODE_LANG ( HIGH_CD, CODE_CD, LANG_CD, CODE_NM, INSERT_DT
) VALUES ( #{HIGH_CD}, #{CODE_CD}, #{LANG_CD}, #{CODE_NM}, NOW()
) ON DUPLICATE KEY UPDATE CODE_NM = #{CODE_NM}, UPDATE_DT = NOW()

mapper.insert:
INSERT INTO TBL_ROLE ( ROLE_SEQ, ROLE_NM, DEL_YN, NOTE, INSERT_DT
) VALUES( #{ROLE_SEQ}, #{ROLE_NM}, #{DEL_YN}, #{NOTE}, NOW()
)

INSERT INTO jmes.TBL_COMPANY
(
      COMP_CD, COMP_NM, CEO_NM, COMP_NUM, FAMILY_COMPANY_YN, BUSINESS_TYPE, BUSINESS_ITEM
    , CHARGE_USER_ID, COMP_EMAIL, COMP_TEL, COMP_FAX, COMP_ADDRESS

        ,LOGO_GFILE_SEQ


        ,SIGN_GFILE_SEQ


        ,ETC_GFILE_SEQ

    , ACTIVE_YN, NOTE, ABBR_NM, COMP_NM2, INSERT_ID
)VALUES (
      #{COMP_CD}, #{COMP_NM}, #{CEO_NM}, #{COMP_NUM}, IFNULL(#{FAMILY_COMPANY_YN}, 'N'), #{BUSINESS_TYPE}, #{BUSINESS_ITEM}
    , #{CHARGE_USER_ID}, #{COMP_EMAIL}, #{COMP_TEL}, #{COMP_FAX}, #{COMP_ADDRESS}

        ,LOGO_GFILE_SEQ


        ,SIGN_GFILE_SEQ


        ,ETC_GFILE_SEQ

    , IFNULL(#{ACTIVE_YN}, 'N'), #{NOTE}, #{ABBR_NM}, #{COMP_NM2}, #{LOGIN_USER_ID}
)

mapper.update:
UPDATE jmes.TBL_COMPANY
SET
     COMP_NM = #{COMP_NM}
    , CEO_NM = #{CEO_NM}
    , COMP_NUM = #{COMP_NUM}
    , CHARGE_USER_ID = #{CHARGE_USER_ID}
    , COMP_TEL = #{COMP_TEL}
    , COMP_FAX = #{COMP_FAX}
    , COMP_ADDRESS = #{COMP_ADDRESS}
    , COMP_EMAIL = #{COMP_EMAIL}

        , LOGO_GFILE_SEQ = #{LOGO_GFILE_SEQ}


        , SIGN_GFILE_SEQ = #{SIGN_GFILE_SEQ}


        , ETC_GFILE_SEQ = #{ETC_GFILE_SEQ}

    , FAMILY_COMPANY_YN = IFNULL(#{FAMILY_COMPANY_YN}, 'N')
    , BUSINESS_TYPE = #{BUSINESS_TYPE}
    , BUSINESS_ITEM = #{BUSINESS_ITEM}
    , ACTIVE_YN = IFNULL(#{ACTIVE_YN}, 'N')
    , NOTE = #{NOTE}
    , ABBR_NM = #{ABBR_NM}
    , COMP_NM2 = #{COMP_NM2}
    , UPDATE_ID = #{LOGIN_USER_ID}
    , UPDATE_DT = NOW()
WHERE COMP_CD = #{COMP_CD}

UPDATE jmes.TBL_COMPANY SET ORDER_COMPANY_YN = (  SELECT CASE WHEN COUNT(*) > 0 THEN 'Y' ELSE 'N' END
                                                  FROM jmes.TBL_COMPANY_TYPE
                                                  WHERE COMP_CD = #{COMP_CD}
                                                    AND HIGH_TYPE = '1024'
                                                )
WHERE COMP_CD = #{COMP_CD}

UPDATE jmes.TBL_COMPANY SET CO_COMPANY_YN = (  SELECT CASE WHEN COUNT(*) > 0 THEN 'Y' ELSE 'N' END
                                                  FROM jmes.TBL_COMPANY_TYPE
                                                  WHERE COMP_CD = #{COMP_CD}
                                                    AND HIGH_TYPE IN ('1001', '1002', '1003', '1004')
                                                )
WHERE COMP_CD = #{COMP_CD}

mapper.select:
SELECT TEMPLATE_SEQ, TEMPLATE_TITLE, TEMPLATE_CONTENT, SEND_NAME, SEND_EMAIL, RECV_EMAIL, CC_EMAIL, INSERT_DT, INSERT_ID, UPDATE_DT, UPDATE_ID FROM jmes.TBL_MAIL_TEMPLATE WHERE 1=1
AND TEMPLATE_SEQ = #{TEMPLATE_SEQ}
AND TEMPLATE_TITLE = #{SEARCH_TITLE}
ORDER BY TEMPLATE_SEQ
