mapper.update:
UPDATE TBL_USER
SET USER_NM = #{USER_NM},
    USER_PWD = #{USER_PWD}
WHERE USER_ID = #{USER_ID}

mapper.delete:
DELETE FROM TBL_USER
WHERE USER_ID = #{USER_ID}
     AND USER_NM = #{USER_NM}

mapper.select:
SELECT HIGH_CD, CODE_CD, (SELECT CODE_NM FROM TBL_CODE_LANG WHERE HIGH_CD = A.HIGH_CD AND CODE_CD = A.CODE_CD AND LANG_CD = 'KR') AS HIGH_NM_KR, (SELECT CODE_NM FROM TBL_CODE_LANG WHERE HIGH_CD = A.HIGH_CD AND CODE_CD = A.CODE_CD AND LANG_CD = 'EN') AS HIGH_NM_EN, DEL_YN, SORT_NUM, REF_CD, NOTE FROM TBL_CODE A WHERE 1 = 1
AND HIGH_CD = #{HIGH_CD}
ORDER BY SORT_NUM

mapper.update:
UPDATE TBL_CODE_LANG
SET CODE_CD = #{CODE_CD},
    CODE_NM = #{CODE_NM},
    UPDATE_DT = current_timestamp(),
    UPDATE_ID = 'xmaseve96'
WHERE HIGH_CD = #{HIGH_CD}
      AND CODE_CD= #{CODE_CD}
      AND LANG_CD = {LANG_CD}
